/* Welcome to BudgetLightForum.com */

div#block-block-1.block.block-block {
    color: #f0f0f0 !important;
    background-color: #292929 !important;
    height: 90px !important;
    padding-top: 1px !important;
}

/* Search */

div#block-block-2.block.block-block {
    height: 75px !important;
}

/* 'back to top' and 'home' and theme logo and some other stuff */
#footer {
    /* display: none !important; */
    color: #bbb;
}

/* 'Back to top' and 'Home' links at bottom of page */
div#block-block-6.block.block-block div.content {
    padding: 0;
    margin: 0;
}
/* original theme logo */
div.rooplelogo {
    display: none !important;
}

/* adsense */
div.block.block--managed {
    display: none;
}

div.forum-post-panel-main.clearfix {
    padding-left: 10px !important;
    padding-right: 10px !important;
    word-wrap: break-word !important;
    background-color: transparent !important;
    color: #ccc;
}

td.views-field.views-field-name {
    width: 150px !important;
    border-style: none !important;
}
div.privatemsg-message-participants {
    color: #888;
    margin-top: 0 !important;
}

#cmf_header {
    padding-top: 11px !important;
}

#watcher_binder {
    padding-bottom: 10px !important;
}

table.sticky-enabled.sticky-table {
    width: 100% !important;
}

/* override style for people who insist on using too much CSS in their posts */
div.posted-by-9004 a {
    text-shadow: none !important;
}

a.watcher_node_toggle_watching_link.watcher-processed.watcher_node_toggle_watching_link_watched {
    font-size: 12px !important;
    font-weight: normal !important;
    color: #2499ff !important;
}

a.watcher_node_help_link_to_binder {
    display: none !important;
}

a {
    border: none !important;
    -webkit-transition: color .3s ease-in !important;
    -moz-transition: color .3s ease-in !important;
    -o-transition: color .3s ease-in !important;
    transition: color .3s ease-in !important;
    /* Make hyperlinks brighter, for DanielM */
    /* color: #0094d9 !important; */
    text-shadow: 0px 0px 5px #000;
}

a:hover {
    color: #fff !important;
    border: none !important;
    text-decoration: none !important;
}

a.active {
    color: #faf !important;
}

/* override when people set a background color on links/spans in forum posts */
div.forum-post-content a, div.forum-post-content span {
    background-color: transparent !important;
}

td.views-field.views-field-comment-count {
    border-style: none !important;
}

tr.odd td {
    border-style: none !important;
}

td.active {
    border-style: none !important;
}

/*
    http://imgur.com/a/RQtbc
    http://imgur.com/a/B3FAu
*/

#content-top {
    background-color: #333 !important;
    margin: 0 !important;
    padding: 0 !important;
}

h1.rtecenter {
    display: none !important;
}

div#block-block-8.block.block-block {
    background-color: transparent !important;
    height: 5px !important;
    margin: 0 !important;
}

#node-admin-filter fieldset legend {
    color: #ffffff !important;
}

label.option {
    color: #e0e0e0 !important;
}

div.forum-topic-header.clear-block {
    padding-top: 10px !important;
}

/* signature at the end of each post */
div.clear {
    /* display: none; */ /* in case you want no signatures at all */
    margin: 6px 0 0 0 !important;
    padding: 0 !important;
    font-size: 80%;  /* make the text smaller by default */
    border-top: 1px solid #555 !important;
}
div.clear div:not([class]) {  /* get rid of '--' divider at top of signature */
    display: none;
}
/* get rid of big tables in signatures (like Bort's) */
div.clear table {
    display: none;
}

div.clear-block {
    border-width: 1px !important;
}

div.block.block-views {
    margin-top: 5px !important;
    border-top-left-radius: 5px !important;
    border-top-right-radius: 5px !important;
}

#cse-search-box div input {
    margin-right: 0 !important;
}

#inner-content {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
}

#squeeze {
    background-color: #292929 !important;
    margin-left: 220px !important;
}

div.content {
    margin-right: 11px !important;
    background: transparent !important;
    background-color: transparent !important;
}

div#block-system-main {
    border-radius: 12px !important;
    padding-top: 1px;
    padding-bottom: 1px;
    margin-bottom: 10px;
}

div.block.block-block {
    padding-bottom: 10px !important;
}

#cse-search-results {
    padding-top: 10px !important;
}

/* text box of the top page title */
div#name-and-slogan {
    margin-left: 20px;
    margin-top: 10px;
}

h2.title {
    padding-top: 10px !important;
}

/* when a notification flashes at the top, it's bright yellow...  yuck */
div.messages.status {
    background-color: #235 !important;
	/* looks better, but prevents notification icons from appearing next to the message */
    /* background: linear-gradient(#235, #57b); */
    border-radius: 10px;
    color: #eee;
}

/* when starting a new thread, the preview is also bright yellow...  yuck */
div.node-preview {
    /* background-color: #235 !important; */
    background: linear-gradient(#333, #444);
    border: 1px solid #666 !important;
    border-radius: 6px;
    color: #bbb;
    padding-top: 8px;
}
div.nf-forum-submitted {
    background: linear-gradient(#444, #333);
    /* background-color: #444 !important; */
    border: 1px solid #666 !important;
    border-radius: 6px;
    margin-top: 0;
}
div.nf-forum-submitted span {
    color: #ccc;
}

/* misc small titles used around the site */
h3 {
    color: #ccc;
}

/* one-line text input boxes */
/* This isn't working, and I don't know why. */
input[type="text"], input[type="password"] {
    background-color: #292929 !important;
    color: #aaa !important;
    border: 2px inset #666;
    border-radius: 4px;
    margin: 0;
    padding-left: 2px;
}
/* form buttons, selection boxes, ... */
input[type="submit"], select, a#edit-cancel, div#privatemsg-filter-tags a, span.flag-wrapper a {
    /* background-color: #aaa !important; */
    background: linear-gradient(#bbb, #666) !important;
    color: #222 !important;
    border: 2px outset #aaa !important;
}
a#edit-cancel, div#privatemsg-filter-tags a, span.flag-wrapper a {
    text-shadow: none !important;
    padding: 1px 6px !important;
    font-size: 90% !important;
    margin-left: 2px !important;
}

/* blocks of preformatted text used to be light grey on white */
/* make it clearly different than a blockquote */
pre {
    background-color: #333;
    border: 2px inset #555;
}

/* Rude-ness meter */
div.userpoints-meter-meter-bar {
    background: linear-gradient(to right, #332b2b, #600) !important;
    border-radius: 4px !important;
    padding: 0 !important;
    margin: 0 !important;
    height: 20px !important;
}
span.points-text {
    padding: 0 !important;
    margin: 0 !important;
    height: 20px !important;
}
div.userpoints-meter-meter-bar div.points-left-wrapper {
    background-image: none !important;
    background: linear-gradient(#800,#f00) !important;
    border-radius: 4px !important;
    padding: 0 !important;
    margin: 0 !important;
    height: 20px !important;
}
div.userpoints-meter-meter-bar div.points-left {
    background-image: none !important;
}
}
