style.css:
@-moz-document domain("animeshow.tv") {
body {
    background:url(/*[[background]]*/) repeat;
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}
span {
    color: white !important;
}

body > header { /*[[headerhide]]*/
}  
  
/*two main homepage images*/

.img_bg3,
.img_bg2 {
    display: none!important;
}
/*all windows colors*/

h1,
h2,
h3 {
    background: #181B1A!important;
    border-bottom: 5px solid rgba(0, 130, 245, 0.9);
    color: cornflowerblue;
}
/*search list color area */

#search_cointainer > ul > li:hover {
    background-color: rgba(0, 0, 0, 0.2);
}
/*top popular box color and removing ugly border*/

.popular_anime_title {
    background-color: rgba(0, 0, 0, 0.6)
}
.popular_anime_hover:focus,
.popular_anime_hover:hover {
    border: 1px solid rgba(0, 130, 245, 0.6);
    box-shadow: 0 0 3px 3px rgba(0, 0, 0, .6)
}
/*entire main outer box color*/

.hidden-xs+ .row {
    background-color: rgba(0, 0, 0, 0.65) !important;
}
/*middle box outer shadow*/

#main {
    background-color: rgba(0, 0, 0, 0.2);
    box-shadow: 0px 0px 30px 10px rgba(255, 255, 255, 0);
}
/*home main background colors body areas*/

#latest_anime .row {
    background-color: #445351;
    margin: 0;
    padding: 10px
}
.latest_episode_title {
    font-size: 12px;
    color: #fff;
}
#popular_anime .row {
    margin: 0;
    background-color: #445351;
    padding: 11px 0 5px 8px;
}
#anime .row,
#new_anime .row {
    background-color: #445351;
}
/*New anime settings below*/

.new_anime_title {
    color: #fff;
}
.new_anime_image img:focus,
.new_anime_image img:hover {
    border: 1px solid rgba(0, 130, 245, 0.6);
    box-shadow: 0 0 3px 3px rgba(0, 0, 0, .6)
}
#new_anime .row {
    margin: 10px 0 0;
    padding: 8px;
}
/*nav*/

#sidebar_content {
    background-color: #172B28;
    box-shadow: none;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    border-bottom-right-radius: 0;
    position: relative
}
#sidebar_content ul li {
    border-bottom: 1.8px dashed rgba(0, 0, 0, .2);
}
#sidebar_content .row {
    background-color: transparent;
}
#sidebar_content .row div:first-child {
    color: #fff;
}
#sidebar_content ul li:nth-child(even) {
    background-color: rgba(0, 0, 0, 0.70);
}
#sidebar_content ul li:nth-child(odd) {
    background-color: rgba(0, 0, 0, 0.70);
}
#sidebar_content ul li:hover {
    background-color: rgba(16, 13, 13, 0.2)
}
/*new text color code*/

.col-xs-2 {
    color: #05ACD4 !important;
}
.col-md-6 {
    color: black !important;
}
/*this part of code is for the other pages*/

#anime_list {
    background-color: #445351;
}
.btn-default {
    background-color: #2E3A48 !important;
}
#anime_list nav .btn:hover {
    color: #05ACD4;
}
/*list settings*/

#anime_list:nth-child(n+1) {
    color: #05ACD4
}
.anime_list_result li a {
    color: white;
}
.anime_list_result h4 a {
    color: #05ACD4
}
#anime_list li a:hover {
    color: #05ACD4;
    text-decoration: none;
}
.anime_list_result hr {
    margin: 5px 0 10px
}
hr {
    height: 1px;
    border: 0;
    border-top: 2.5px solid #00D0FF;
    margin: 1em 0
}
/*episode list settings*/

#episodes_list,
#anime {
    background-color: rgb(68, 83, 81);
}
.episodes_list_hover {
    color: #FFF;
}
.episodes_list_result:hover {
    background-color: rgba(16, 13, 13, 0.5);
    cursor: pointer
}
#anime > div.row > div.col-lg-6.col-md-6.col-sm-12.col-xs-12.anime_info > div:nth-child(n+1) > div.col-lg-3.col-md-3.col-sm-3.col-xs-3 {
    color: white;
}
#anime > div.row > div.col-lg-6.col-md-6.col-sm-12.col-xs-12.anime_info > div:nth-child(n+1) > div.col-lg-9.col-md-9.col-sm-9.col-xs-9 {
    color: white;
}
.alternative_titles,
.anime_discription {
    color: white;
}
/*this part of code is for the main shiz*/

#episode_additional_informations {
    display: none!important
}
#episode {
    background-color: rgba(25, 23, 23, 0.79);
}
#episode > p.mirrors_info {
    display: none!important
}
.episode_mirrors_name {
    color: #457CAF;
}
.episode_mirrors_type_sub,
.episode_mirrors_time {
    color: #687B78;
}
.episode_title {
    color: #257494;
    text-align: center
}
#episode_navigation a {
    color: #1A4F65;
    text-decoration: none!important;
}
#episode_navigation a:hover {
    color: #05ACD4;
    text-decoration: none!important;
}
/*disqus area*/

#episode_disqus {
    background-color: rgba(25, 23, 23, 0.79);
}
/*stupid part*/

#new_anime,
#latest_anime,
#popular_anime {
    background: rgb(67, 80, 78) !important;
}
/*responsive mobile mode colors*/
/*nav and hover color for nav*/

.navbar-static,
div#search_cointainer {
    background: rgba(21, 27, 26, 0.99)!important;
}
.nav > li:hover {
    background: rgba(0, 0, 0, 0.4) !important;
}
#nav .container {
    background-color: rgba(21, 27, 26, 0.74)
}
#nav ul li {
    border: none;
}
#nav ul li a {
    color: #fff;
    height: 100%;
    font-size: 16px
}
#nav ul li:hover {
    background: rgba(0, 0, 0, 0.4) !important;
}
.anime_list_result li {
    border-bottom: none;
}
/*Genre checker is back yay*/

#genres,
#genres_result {
    background-color: #445351;
    margin-top: 10px;
}
#genres_list .row div {
    padding: 4px 0;
    font-size: 14px;
    color: #fff;
}
.genres_result_title {
    color: #fff;
}
.main .row div {
    color: #fff;
}
.genres_result_hover:hover {
    background-color: rgba(0, 0, 0, 0.15);
}
.anime_info a {
    font-size: 13px;
    font-weight: 700;
    color: #059AE1;
}
.ads {
    display: none!important
}
/*bottom bits*/

body > div.container.main > footer {
    display: none!important
}
}

@-moz-document domain("disqusads.com") {
/*Removing disqus ads*/

html {
    display: none!important;
}
.dark,
.text-subheading,
#grv_widget,
#grv_badge,
#grvWidgetStylesLoaded_disqus_eop_resp_6_v3_all_themes_teardrop_v2_f748720cf0790c7e1059f644bc86226c_css {
    display: none!important;
}
}

@-moz-document domain("mp4upload.com") {
#overlay2{
    display: none!important;
}
}
