style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("minvei.no") {
/*
 * Version 0.2, 2016-02-19
 **/
  .framework #wrapper_page {
    width: 100%;
    max-width: none!important; /* Set because max-width is originally set to a fixed px size */
  }
  #wrapper_top_row { /* To make the top banner look nice. Bg-color matches banner picture */
    background-repeat: no-repeat;
    background-color: #459395;
  }
  #wrapper_middle_row { /* Margins matches the widths of element_cell_left and _right - the left and right menus */
    margin-left: 300px;
    margin-right: 175px;
  }
  #element_cell_left { 
    width: 300px;
  }
  #wrapper_all_content {
    margin-left: 175px;
  }
  #element_cell_right {
    right: 0;
    width: 175px;
  }
  /* Removing sideways scroll on long menu links by allowing word-wrap: */
  #element_menu li {
    white-space: normal;
    text-overflow: inherit;
    max-width: 255px;
  }
  #element_menu a {
    white-space: inherit;
    text-overflow: inherit;
  }
}
