style.css:
@-moz-document url-prefix("http://localhost:9510/web/") {
body {
    color            : #EEE;
    background-color : #111;
}
.main {
    background : #222;
}
a:hover {
    color : #2A6496;
}
.nav a {
    color : #AAA;
}
.nav a:hover,
.nav a.active,
.log-msg,
input[type="number"],
input[type="password"],
input[type="text"],
.button,
.form-control {
	color : #FFF;
}
.button {
	background-color : #333;
}
.log,
input[type="number"],
input[type="password"],
input[type="text"] ,
.button:hover ,
.form-control {
    background-color : #111;
}
.dragdrop {
    background : rgba(0,0,0,0.66);
}
.dragdrop-content {
    color : black;
    box-shadow : 0 2px 5px 0 rgba(0,0,0,1);
}
.dragdrop-content .button {
    background: #111;
}
}

@-moz-document url-prefix("http://localhost:9510/system/config") {
body {
	background-color : #111;
    color            : #EEE;
}
}

@-moz-document url-prefix("http://localhost:9510/client/") {
body {
    background-color : #111;
    color            : #EEE;
}
.nav a {
    color : #DDD;
}
.nav a:hover,
.nav a.active {
    background : #333;
    color      : #FFF;
}
.nav-middle li.active {
    background : #000;
}
.nav-brand:hover {
    background : #333 !important;
}
.main {
    background : #222;
}
.button {
    background-color : #646464;
    color            : #EEE;
}
a:hover {
    text-decoration: none;
    color : #FFF;
}
.button:hover {
    background: #111;
}
}
