style.css:
@-moz-document domain("www.bilibili.com") {

/*字体*/

*:not([class*="icon"]):not(i):not(pre):not(code):not(kbd):not(samp):not(var) {
    font-family: "Microsoft Yahei" !important;
    text-shadow: 0em 0em 0.03em #999999 !important;
}

.scontent div.time-label {
    font-family: "Arial" !important;
}

pre, code, kbd, samp, var {
    font-family: "Monaco", "Consolas", "Microsoft Yahei";
    text-shadow: 0em 0em 0.03em #999999 !important;
}
/*背景*/

body, .b-page-body, .comm div, .upinfo, .v_info, .rank-list-container, .ov-box .ov, .v_bgm_list, .upload-user-text, .a-layer, .l-item, .elecrank-header a {
    background: #F9F9F9 !important;
}

.footer, .footer .footer-wrp, #cb_controler {
    background: #EAEAEA !important;
}
.b-toggle-btn {
    -webkit-filter: invert(2%)!important;
}

/*播放器区域*/

.player-wrapper {
    background: url(http://ww4.sinaimg.cn/large/005Suejwgw1f157kkq5lyj31hc0u0tet.jpg);
    background-position: right bottom;
    background-size: cover;
}

.scontent {
    overflow: hidden !important;
    border-radius: 5px !important;
}

.scontent:not(.float) {
    box-shadow: 0px 0px 30px rgba(66, 133, 244, 255), 0 0 30px rgba(0, 0, 0, 0.23) !important;
}

.player-wrapper .arc-toolbar {
    box-shadow: 0px 0px 15px rgba(66, 133, 244, 255), 0 0 5px rgba(0, 0, 0, 0.23) !important;
}

.ABP-Mini {
    box-shadow: 0px 3px 5px #bbb, 0 0 10px rgba(0, 0, 0, 0.23) !important;
}

.scontent.float {
    overflow: visible !important;
}

.scontent.float .move {
    overflow: hidden !important;
    border-radius: 4px 4px 0 0 !important;
}
/*头部*/

.header div.num {
    box-shadow: 0px 1px 5px #bbb, 0 0 10px rgba(0, 0, 0, 0.23) !important;
}

.nav-menu {
    border: none !important;
}

.container-top-wrapper, .b-page-body {
    margin-top: 20px !important;
}

.header-layer, .b-header-mask-wrp .b-header-mask-bg {
    background: url(http://ww1.sinaimg.cn/large/005Suejwgw1f168lyzuwlg30b40b4gln.gif) !important;
    background-size: 170px 170px !important;
}

.my-space .b-header-mask-bg {
    background: #fff!important;
}

.banner-title, .bili_live_pmt, .header-link {
    display: none !important;
}

.b-header-blur .menu .i-link, .b-header-blur .z_top_nav a, .b-header-blur .z_top_nav span {
    color: white!important;
    text-shadow: 0 0 20px #fff !important;
}

.h-center a.logo {
    background-image: url(http://ww2.sinaimg.cn/large/005Suejwgw1f163talpwyg304405imx0.gif) !important;
    background-size: contain !important;
    background-position: right !important;
    background-repeat: repeat-x !important;
    width: 1000px !important;
    height: 100px !important;
    left: -850px !important;
    top: 60px !important;
    pointer-events: none;
}

.h-center a.logo:before {
    content: "";
    position: absolute !important;
    right: -130px !important;
    top: -8px !important;
    width: 180px !important;
    height: 105px !important;
    background-image: url(http://ww4.sinaimg.cn/large/005Suejwgw1f1627awqwmg307k04oaaj.gif) !important;
    background-repeat: no-repeat !important;
    background-size: contain !important;
    background-position: right !important;
    pointer-events: auto!important;
}

#searchform, .link-ranking {
    box-shadow: 0px 0px 15px rgba(66, 133, 244, 255), 0 0 10px rgba(0, 0, 0, 0.23) !important;
}

.link-ranking {
    left: -3px !important;
}
/*小区块*/

.charge-support {
    top: 10px;
    box-shadow: 0px 3px 8px #bbb, 0 0 10px rgba(0, 0, 0, 0.23) !important;
}

.v .preview {
    border: 2px solid #ddd;
}

.v .preview:hover {
    border: 2px solid #00a1d6;
}

#comment_text {
    box-shadow: 2px 2px 3px #ddd, 0 0 0px rgba(0, 0, 0, 0.23) !important;
}

.r-info {
    border-radius: 4px;
}

.viewbox .upinfo{
    margin-top: 10px !important;
    margin-bottom: 10px !important;
}
.viewbox .info{
    margin-top: -10px !important;
}
#date_select {
    box-shadow: rgba(0,0,0,0.16) 0 1px 2px !important;
    border-radius: 4px;
}
.vd-list-cnt {
    padding-bottom:30px!important;
}
/*按钮*/

.pointer-block, .layer-follow-btn, .n-i.customize:hover, button.submit-comment, .b-btn.f:not(.on), .pagelistbox .p.active, .pagelistbox .p:hover{
    background: url("data:image/png;base64,") !important;
    box-shadow: 0px 0px 20px #00a1d6 inset,rgba(0,0,0,0.16) 0 1px 2px !important;
}
.index-nav .n-i.gotop .btn_gotop:hover {
    background:url(http://static.hdslb.com/images/base/icons.png) ,url("data:image/png;base64,");
    background-position:-713px -72px, center;
    box-shadow: 0px 0px 20px #00a1d6 inset, 0px 1px 2px #bbb, 0 0 8px rgba(0, 0, 0, 0.23) !important;
}

.pointer-block:hover, .layer-follow-btn:hover, button.submit-comment:hover, .b-btn.f:not(.on):hover {
    background: url("data:image/png;base64,") !important;
    box-shadow: 0px 0px 35px #00a1d6 inset,rgba(0,0,0,0.16) 0 1px 2px !important;
}

.n-i.sortable .name:hover {
    background: url(http://ww2.sinaimg.cn/large/005Suejwgw1f163talpwyg304405imx0.gif) repeat-x !important;
    background-size: auto 33px !important;
    background-position: left top !important;
    font-weight: bold;
}

.btn_gotop, .nav-list {
    box-shadow: 0px 1px 2px #bbb, 0 0 8px rgba(0, 0, 0, 0.23) !important;
}
.pagelistbox.small .p:link {
    background: #F9F9F9;
}
.read-push, .b-link-more a , .b-slt, .more-link a, .b-btn:not(.dy), .b-input, .ywz-wrapper .ywz, .bangumi-order-btn {
    box-shadow:rgba(0,0,0,0.16) 0 1px 2px !important;
}
.ywz-wrapper {
    top:5px;
}
.tag-list li {
    box-shadow:rgba(0,0,0,0.16) 0 1px 2px !important;
}
.tag-list li:not(.unverified) {
    background-color:#FFF !important;
}
.pagelistbox:not(.small) a {
    box-shadow:rgba(0,0,0,0.16) 0 1px 2px !important;
}
.pagelistbox {
    overflow:visible;
    height:40px;
}
#v_bgm_list_data a:not(.active) {
    background:#fff;
    box-shadow:rgba(0,0,0,0.16) 0 1px 2px !important;
}
#v_bgm_list_data a.active, #v_bgm_list_data a:hover {
    background: url("data:image/png;base64,") !important;
    box-shadow: 0px 0px 35px #00a1d6 inset,rgba(0,0,0,0.16) 0 1px 2px !important;
}

/*文字*/

.pagelistbox .p.active, .pagelistbox .p:hover {
    color: white !important;
}

.player-wrapper .b-head-t:not(:hover) a {
    color: white !important;
    text-shadow: 0px 0px 10px white !important;
}

.player-wrapper .b-head-t a {
    font-weight: normal !important;
}
}

@-moz-document domain("www.bilibili.com") {
/*广告屏蔽*/

#b_promote, #b_recommend, .menu-r, .v-live-recommend, li.b-gc, li.yoo, .mobile-link-l {
    display: none !important;
}

.n-i-mlink {
    visibility: hidden !important;
}

.header {
    background: none !important;
}

.menu-wrapper {
    width: 820px !important;
}

.widescreen .menu-wrapper {
    width: 978px !important;
}
}

@-moz-document domain("www.bilibili.com") {
/*bilibili plugin*/
.block.helper{
    float:left !important;
}
}

@-moz-document domain("www.bilibili.com") {
@keyframes slideInDown {
    0% {
        transform: translateY(-50%);
    }
    100% {
        transform: translateY(0);
    }
}

.ABP-Comment-List span {
    animation:none !important;
}
.i_num {
    animation: fadeInDown 0.3s !important;
    transition:none !important;    
}
}
