style.css:
@-moz-document url-prefix("https://whackahack.com/foro/"), domain("wahackforo.com"), domain("foro.whackahack.com") {
body {
    background-image: none;
    /*background-color: #3C3C3C;*/
    background-color: #f7ebe1;
}
#infoarea-webforo {
    width: 100%;
    height: 165px !important;
    top: 35px !important;
    left: 0 !important;
}
.infoarea-web {
    display: none;
    width: 0 !important;
}
#infoarea-foro {
    height: 100% !important;
    width: 100% !important;
}
.tborder.alert {
    padding: 3px 0;
    color: #b36531;
}
.tcat {
    background-image: none;
    background-color: #94c4d3;
    text-shadow: none;
    border-radius: 0;
}
#qrform .tcat {
    background-color: #e0a058;
}
.tcat,
.tcat a:link,
.tcat a:visited {
    color: #222;
}
#forum_container {
    border-radius: 0;
    border: 1px solid;
    box-shadow: none;
}
#cybstats_lpdiv a {
    display: inline-block;
    max-width: 40em;
    text-overflow: ellipsis;
    overflow: hidden;
}
.vbseo_links {
    visibility: visible !important;
}
#header {
    /*background-image: url(http://wahackforo.com/estilo/vbulletin5_logo.png);*/
    /*background-image: none;*/
    /*background-image:url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' version='1.1' height='200px' width='700px'> <text x='35' y='125' fill='black' font-size='80' font-family='sans-serif'>Whack a Hack!</text></svg>");*/
    background-image: url(https://www.cosarara.me/images/wah_banner.png);
    /*background-color: #E49630;*/
    background-color: #898989;
    border-radius: 0;
    border: 1px solid;
    border-bottom: none;
    border-color: #CE865F;
    width: auto;
}
.post-entry img {
    max-width: 100% !important;
}
.post-entry table {
    table-layout: fixed;
}
.post_button {
    margin: 0;
}


/*#header:after {
    content: "lmao";
    font-size: 40px;
    text-align: center;
    
  }*/
.upper_panel {
    text-shadow: none;
    font-weight: normal;
    color: #111;
}
.upper_panel strong {
    font-weight: normal;
}
.upper_panel a {
    color: #3B3B3B;
    font-weight: normal;
}
.lower_panel {
    /*border: 1px solid;
    border-top: none;
    border-color: #CE865F;*/
    background-color: #CE865F;
}
.lower_panel a.logout {
    color: #000;
}
.lower_panel,
.lower_panel a,
.lower_panel a:visited {
    color: #111;
}
.tborder {
    border-radius: 0 !important;
    padding: 0;
    box-sizing: border-box;
    border: 1px solid;
    border-color: #3d86b0;
}
#posts .forum_tborder,
#qrform .forum_tborder {
    border-radius: 0 !important;
    padding: 0;
    box-sizing: border-box !important;
    width: 940px !important;
    border: 1px solid !important;
    border-color: #e0a058 !important;
}


/*.forum_tborder.tcat {
    
  }*/
.forum_tborder td {
    padding-top: 4px;
    padding-bottom: 4px;
}
.thead.post {
    background-color: #e0a058;
    border-radius: 0 !important;
    color: #222;
}
.thead.post a,
.thead.post a:visited {
    color: #111 !important;
}
.forumbutton {
    background-color: #A25794;
}
.vbmenu_control {
    background-color: #3982B3;
}
.alt2 img {
    height: auto;
    width: auto;
}
.controlbar {
    padding-right: 25px !important;
}
#collapseimg_cyb_fh_stats {
    box-sizing: border-box;
    width: 8px;
    height: 8px;
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="8" height="8" viewBox="0 0 8 8">  <path d="M3 0v3h-3v2h3v3h2v-3h3v-2h-3v-3h-2z" /></svg>');
    padding-left: 8px;
}
.tcat_vbmenu .vbmenu_control {
    background-image: none;
    background-color: #3982B3;
}
.tcat_vbmenu tr td:last-child,
.tcat_vbmenu tr td:first-child {
    border-radius: 0;
}
.floatcontainer {
    margin-top: 10px;
}
ul.tab_list li.thead {
    border-bottom-color: #E5E5E5;
}
#collapseimg_cyb_fh_stats,
.tcat a img,
#collapseimg_forumhome_activeusers,
#collapseimg_forumhome_todayusers,
#collapseimg_forumhome_stats,
#collapseimg_forumhome_birthdays,
#collapseimg_forumrules {
    box-sizing: border-box;
    width: 8px;
    height: 8px;
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="8" height="8" viewBox="0 0 8 8">  <path d="M3 0v3h-3v2h3v3h2v-3h3v-2h-3v-3h-2z" /></svg>');
    padding-left: 8px;
    margin: 3px;
}


