#vbseo-likes {
    height: auto;
}
.vbseo-likes-count {
    font-size: 12px;
    font-family: sans-serif;
    padding: 2px;
    height: auto;
}
.vbseo-likes-count-image {
    position: static;
    float: left;
    margin-right: 8px;
    box-sizing: border-box;
    width: 13px;
    height: 13px;
