.signature {
    background: none;
}
.alt1 {
    color: #333;
}
.spoiler h3 {
    color: #333 !important;
    font: 14px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
    font-weight: bold !important;
    text-shadow: none !important;
}
}

@-moz-document url-prefix("http://wahackforo.com/newthread.php"), url-prefix("https://whackahack.com/foro/newthread.php") {
.alt1 {
    display: block !important;
    padding: 5px !important;
    overflow-x: auto !important;
    width: 918px !important;
}
.alt1 img {
    max-width: 100% !important;
}
.alt1 table {
    table-layout: fixed;
}
}
