style.css:
 @-moz-document domain('deviantart.com'){

    /*Plain text*/
    
    body {
        background: #111!important;
        color: #FFF!important;
        /*affects artwork description color and many other*/
        
        text-shadow: 0px 0px 0px #1F261F!important;
        /*set to 0px to hide shadow and make text more crisp*/
        /*font-family: "Segoe UI";*/
    }
    #content {
        color: #EDF5DD!important;
        text-shadow: 1px 1px 1px #1F261F!important
    }
    /*Favorites button (top right) drop down*/
    
    body.mc-stacks div.mczone h2.mczone-title div.mcviews a {
        background-image: url(http://i1365.photobucket.com/albums/r749/King-Gigabyte/Burnin%20Orange%20CSS/buttons%202_zpsyohabrug.gif) !important;
    }
    html>body #lub div.collections {
        background: #222
    }
    .status-form .status-entry {
        background: #222;
        border: 1px solid #a5afa5;
        border-radius: 3px;
        position: relative;
        padding: 6px 5px 4px;
    }
    .smbutton.status-preview {
        color: #fff !important;
    }
    .status-form .inputs .smbutton.status-submit {
        background: #d86d00;
    }
    .smbutton span {
        border-top: 0;
        padding: 10px 9px;
        text-transform: uppercase;
        font: 12px Helvetica Neue, Arial, sans-serif;
        text-shadow: 0 1px 1px rgba(120, 120, 120, 1);
        letter-spacing: .5px;
    }
    .smbutton.smbutton-blue,
    .smbutton.smbutton-blue.disabledbutton:hover,
    .smbutton.smbutton-blue.disabledbutton:active,
    body.stash-page .smbutton.smbutton-blue {
        border-color: #7a3e00;
        background: #0088b5;
        background: -moz-linear-gradient(top, #d86d00, #a3580c);
        background: linear-gradient(top, #d86d00, #a3580c);
        color: #fff;
        text-decoration: none;
    }
    .feed-action .feed-action-comments .ccomment-form .cctextarea {
        margin-bottom: 0;
        padding-left: 30px;
        padding-bottom: 4px;
        background: #545454;
        border-radius: 4px;
        box-shadow: none;
        border: 1px solid #d4decf;
    }
    :link {
        color: #ffd27c!important;
    }
    :link,
    :visited {
        color: #d6914a!important;
    }
    a {
