    #ile-container .ile-left .ile-left-panel {
        color: #FFF!important;
    }
    #ile-contents .ile-tags .tags-widget {
        background: #222!important;
        border: 1px solid rgb(211, 211, 211);
        box-shadow: 0px 0px 0px 0px rgb(246, 246, 246) inset;
