    #modal-form-category div.pager-light div.page2 a.selected,
    #modal-form-category div.pager-light div.page2 a.more-selected,
    .editmode div#ile-contents #modal-form-category div.pager-light div.page2 a.selected,
    .editmode div#ile-contents #modal-form-category div.pager-light div.page2 a.more-selected {
