        background: #222!important;
    }
    .inline-category-picker #modal-form-category div.pager-panes {
        background: #222!important;
    }
    /*selected category(final preview)*/
    
    .ile-section-category .cat-preview .preview-text h3 {
        color: #FFF;
    }
    .ile-section-category .cat-preview .preview-text .cat-name {
        color: #0055AA;
    }
    .ile-section-category .cat-preview {
        background: #222;
    }
