    /*G1e: Submit page =======================================================================================================*/
    /*G1s:Support Section (Customer Service Central) ===================================================================*/
    /*Header Bar*/
    
    #header {
        padding: 0px;
        height: 60px;
        background: #1a1a1a none repeat scroll 0% 0%;
        position: relative;
    }
    /*Navigation Bar Background*/
    
    #top-menu-background {
        border-top: 1px solid #D8E2D6;
        background: #002200;
    }
    /*Navigation Bar Font*/
    
    #top-menu a.tab,
    #top-menu ul#green a.tab {
        border-bottom-width: 0px !important;
        border-top-left-radius: 5px;
        border-top-right-radius: 5px;
        color: #FFF;
    }
    /*G2s: Entry Page ++++++++++++++++++++++++++++++++++++++++++*/
    /*Page background*/
    
    div#container {
        border-radius: 0px;
        background: #111;
    }
    /*Original Post*/
    /*Title*/
    
    .entry h3.entry-title {
        color: #FFF;
        background: #000;
    }
    div.frame div.entry div.entry-container {
        background: #000;
    }
    body.entries-show .entry p.entry_user {
        padding: 0px 7px 4px;
        background: #000;
        color: #fff;
    }
    body.entries-show .entry img.entry_user {
        border-width: 0px 0px 0px 1px;
        border-left: 1px solid #F2FCF1;
        padding: 0px 0px 6px 33px;
        background-color: #000;
    }
    /*Content*/
    
    .user_formatted strong {
        color: #FFFFFF;
    }
    .entry:last-child {
        border-bottom: 1px solid #A6B2A6 !important;
        background: #222;
    }
    .content p {
        color: #FFF;
    }
    /*Comments*/
    
    #history {
        padding: 15px;
        color: #FFFF99;
        background: #222;
    }
    #history .item p {
        margin: 0px 0px 12px;
        font-size: 12px;
        color: #FFF;
    }
    /*Popular Status Posts box*/
    
    .side-box-content h3 {
        font-family: "Trebuchet MS", sans-serif;
        font-weight: bold;
        letter-spacing: -1px;
        color: #FFF;
    }
    #sidebar .side-box-content {
        width: 209px;
        padding-right: 16px;
        padding-left: 16px;
        background: #222;
    }
    .blue_box_top {
        background: #222;
    }
    .blue_box_bottom {
        background: #222;
    }
    /*Knowledge Base Right Side Bar Box*/
    
    #sidebar .widget h3 a {
        color: #FFF;
    }
    /*G2e: Entry Page ++++++++++++++++++++++++++++++++++++++*/
    /*G2s: Check your existing requests+++++++++++++++++++++++++++++++++*/
    /*Request background*/
    
    .ticket .comment {
        clear: both;
        padding-bottom: 15px;
        background: #222;
    }
    /*Request Title Font*/
    
    .ticket h3 a {
        color: #00AAFF;
        text-decoration: none;
    }
    /*Request Content Font*/
    
    .ticket .comment .message p {
        margin: 2px 10px 6px 0px;
        line-height: 1.5em;
        color: #FFF;
    }
    /*G3s: Compact List ---------------------------------------*/
    /*Table background, Dark Accent*/
    
    table.tickets tr.linked:nth-child(2n+1) {
        background-color: #000;
    }
    /*Table background, Light Accent*/
    
    table.tickets tr.linked {
        cursor: pointer;
        font-size: 12px;
        background: #222;
        color: #FFF;
    }
    /*Table background (hover)*/
    
    table.tickets tr.linked:hover > td {
        background-color: #333333;
    }
    /*Columns Font*/
    
    table.tickets td {
        border-color: #FFF;
        color: #FFF;
    }
    /*Hover over row preview popup*/
    
    .ui-tooltip.zd_comment {
        background: #222;
    }
    .ui-tooltip.zd_comment .title {
        color: #FFF;
        background: #000;
    }
    .ui-tooltip.zd_comment .content {
        background: #222!important;
    }

