.pagination-default >*,
.pager >*,
.j_pager >*,
.pb_list_pager >* {
	color: var(--m-light-40) !important;
	float: none !important;
	display: inline-block !important;
	text-align: center;
	min-width: 13px !important;
	line-height: 25px !important;
	height: 25px !important;
	padding: 0 10px !important;
	margin: 0 !important;
	border: none !important;
	overflow: visible !important;
	background: var(--m-block-d005) !important;
	border-radius: 12.5px !important;
	transition-property: background;
	transition-duration: .5s;
	transition-timing-function: cubic-bezier(0.23, 1, 0.32, 1);
}

.pagination-default >.pagination-current,
.pager > .cur,
.j_pager > .tP,
.pb_list_pager > .tP {
	color: #fff !important;
	background: hsla(0,0%,var(--m-block-darken-lightness),calc(0.05*var(--m-block-darken) + 0.12)) !important;
	width: auto !important;
}

.itb_pager >a:hover,
.pagination-default >a:hover,
.pager >a:hover,
.j_pager >a:hover,
.pb_list_pager >a:hover {
	background: hsla(0,0%,var(--m-block-darken-lightness),calc(0.05*var(--m-block-darken) + 0.06)) !important;
}

/*【发贴编辑框】*/
.tb_rich_poster {
	margin: 0 20px !important;
}

.tb_rich_poster_container {
	width: 100% !important;
	padding: 0 !important;
	padding-top: 20px !important;
}

.poster_body {
	width: 100% !important;
	margin: 0 !important;
	padding: 0 !important;
}

.poster_head {
	border: none !important;
	width: 100% !important;
	margin: 0 !important;
	padding: 0 !important;
	margin-bottom: 10px !important;
	line-height: 28px;
}

.poster_head_text {
	width: auto !important;
	padding: 0 10px !important;
	background: var(--m-block-d006);
	border-radius: 6px;
	color: var(--m-light-60);
	font-weight: normal !important;
	font-size: 14px !important;
}

.poster_head_text>a {
