	text-align: var(--m-editortitle) !important;
}

.editor_content_wrapper {
	width: 100% !important;
	margin: 0 !important;
	padding: 0 !important;
	background: none !important;
	box-sizing: border-box;
}

.old_style_wrapper {
	width: 100% !important;
	background: none !important;
	margin: 0 !important;
	padding: 0 !important;
	border: none !important;
	box-sizing: border-box;
}

.poster_body .edui-container {
	width: 100% !important;
	background: none !important;
	margin: 0 !important;
	padding: 0 !important;
}

.poster_body .edui-body-container {
	min-height: 216px !important;
	width: 100% !important;
	margin: 0 !important;
	padding: 0 !important;
}

.edui-body-container *::selection {
	background-color: rgba(0,0,0,calc(0.12*var(--m-block-darken)));
	text-shadow: none;
}

.tb_poster_placeholder {
	position: absolute !important;
	left: 0 !important;
	top: 55px !important;
	width: 100% !important;
	padding: 0 15px !important;
	box-sizing: border-box;
	color: var(--m-light-60) !important;
}

.tb_poster_placeholder p {
	width: 100% !important;
}

/*编辑框控件域*/
.edui-toolbar {
	background: var(--m-block-d008) !important;
	box-sizing: border-box;
	border-bottom: 4px solid hsla(0,0%,var(--m-block-darken-lightness),0.1);
	height: 40px !important;
	margin: 0 !important;
	padding: 0 !important;
}

.poster_body .edui-editor-body .edui-body-container {
	padding: 0 10px !important;
}

.poster_body .editor_title:focus,
.poster_body .edui-editor-body .edui-body-container:focus {
	background: var(--m-block-d002) !important;
	border-bottom: 4px solid hsla(0,0%,var(--m-block-darken-lightness),0.2) !important;
}

.edui-popup {
	z-index: 9 !important;
}

.edui-btn-toolbar {
	position: absolute;
	top: 0;
	width: 100% !important;
	background: none !important;
	padding: 0 !important;
	height: 40px !important;
	line-height: 36px !important;
	box-sizing: border-box;
}

.edui-btn {
	height: 40px !important;
	display: block !important;
	width: 60px !important;
	margin: 0 !important;
	padding: 0 !important;
	float: left !important;
	background: none !important;
	overflow: hidden;
	cursor: pointer;
	box-sizing: border-box;
	border-bottom: 4px solid rgba(0,0,0,0) !important;
	transition-property: border;
	transition-duration: 1s;
	transition-timing-function: ease;
}

.edui-toolbar .edui-btn-red,
.edui-toolbar .edui-btn-bold,
.edui-toolbar .edui-btn-fullscreen {
	width: 36px !important;
	float: right !important;
}

.edui-toolbar .edui-btn[style*="none"] {
	display: none !important;
}

.edui-btn:hover {
	border-bottom: 4px solid rgba(0,0,0,.2) !important;
}

.edui-btn:hover .edui-icon {
	transform: translateY(-38px);
}

.edui-icon {
	height: 36px !important;
	background: none !important;
	margin: 0 !important;
	padding: 0 !important;
	width: 100% !important;
	transition-property: transform;
	transition-duration: .5s;
	transition-timing-function: cubic-bezier(0.23, 1, 0.32, 1);
}

.edui-icon:before {
	content: "\e22a";
	font-family: "Material Icons";
	display: block;
	color: var(--m-clight-60);
	font-size: 24px;
	position: absolute;
	left: 50%;
	top: 50%;
	transform: translate(-50%, -50%);
	-moz-transform: translate(-50%, -40%);
}

.edui-icon:after {
	content: "未定义";
	display: block;
	color: var(--m-clight-40);
	font-size: 12px;
	position: absolute;
	left: 50%;
	top: 50%;
	transform: translate(-50%, -50%) translateY(38px);
	white-space: nowrap;
}

.edui-icon-fullscreen:before {
	content: "\e5d0";
}

.tb-ueditor-fullscreen .edui-icon-fullscreen:before {
	content: "\e5d1";
}

.tb-ueditor-fullscreen .edui-icon-fullscreen:after {
	content: "还原";
}

.edui-icon-fullscreen:after {
	content: "全屏";
}

.edui-icon-bold:before {
	content: "\e238";
	margin-top: 1px;
}

.edui-icon-bold:after {
	content: "加粗";
}

.edui-icon-red:before {
	content: "\e23c";
	font-size: 18px;
}

.edui-icon-red:after {
	content: "红字";
}

.edui-icon-paypost:before {
	content: "\e263";
}

.edui-icon-paypost:after {
	content: "付费可见";
}

.edui-icon-medal:before {
	content: "\e838";
}

.edui-icon-medal:after {
	content: "贴吧特权";
}

.edui-btn-name-portrait .edui-icon-medal:before {
	content: "\e253";
	font-size: 20px;
}

.edui-btn-name-portrait .edui-icon-medal:after {
	content: "发贴气泡";
}

.edui-icon-image:before {
	content: "\e251";
}

.edui-icon-image:after {
	content: "图片";
}

.edui-icon-video:before {
	content: "\e02c";
}

.edui-icon-video:after {
	content: "视频";
}

.edui-icon-music:before {
	content: "\e405";
}

.edui-icon-music:after {
	content: "音乐";
}

.edui-icon-formula:before {
	content: "\e24a";
}

.edui-icon-formula:after {
	content: "数学公式";
}

.edui-icon-emotion:before {
	content: "\e24e";
}

.edui-icon-emotion:after {
	content: "表情";
}

.edui-icon-scrawl:before {
	content: "\e3b7";
}

.edui-icon-scrawl:after {
	content: "涂鸦";
}

.edui-icon-attachment:before {
	content: "\e2bc";
	font-size: 26px;
}

.edui-icon-attachment:after {
	content: "附件";
}

.edui-icon-quick-reply:before {
	content: "\e539";
}

.edui-icon-quick-reply:after {
	content: "快速回贴";
}

.edui-icon-topic:before {
	content: "#";
	font-family: fantasy;
	font-size: 24px;
	font-weight: bold;
	margin-top: -0.5px;
	-moz-transform: translate(-50%, -46%);
}

.edui-icon-topic:after {
	content: "话题";
}

