.l2 {padding-bottom: 20px !important}


/* chat contact list hover */
.Ia .Bb:hover,
/* chat box header bottom border */
.MT,
/* chat box buttons hover */
.Ik .qp:hover {background-color: #60911c !important}
/* chat box header */
.Ik {background: #3b3b4e !important}
/* chat box input */
.kq > .My {border-color: #60911c !important}

}
