style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://i.hdu.edu.cn/") {
  
  #header :not(.exit),
  #container,
  #homeMiddelBannerWidet-content,
  #wk_fourHomeWidget-content,
  #wk_weatherHomeWidget-content,
  .left,
  .work_windows,
  .app_list_btn_box,
  .application_list_explain,
  .wk_midpimwidget,
  #footer,
  .app_btn_gray_gear,
  div.quickhome_title.hp_title,
  #wk_calendarHomeWidget-content,
  .application_list_title,
  .wk_applicationlistwidget,
  .user_brief_info,
  #innerdiv:first-child,
  .circle_space_left,
  li [train_id] :not(.last),
  #head_sculpture,
  a[src*="3668"],
  .meeting_notice_content,
  .meeting_notice_title,
  .margin_top_10,
  #outdiv,
  #container,
  div.fancybox-bg
  {
    visibility: hidden  !important;
  }
/*被隐藏的内容*/
  
  #header,
  #footer,
  .post_close,
  #addEmail
  {
    display: none;
  }
/*  被隐藏的内容  */
  
  #container_content
  {
    height: 0px !important;
    width: 0px !important;
    min-height: 0;
    min-width: 0;
  }
/*  减小页面宽度和高度  */
  
  .user_name a,
  .meeting_notice_content,
  .quickhome_item_link font,
  .links,
  span.highlight,
  .quickhome_item_close,
  .user_name
  {
    visibility: visible !important;
    display: block !important;
  }
/*要显示的内容*/
  
/*>>>>>>>>>>>>>自定义Style开始<<<<<<<<<<<<<*/
  
  body{
    background-color: #333333;
    background-image: url(/*[[picurl]]*/);
/*     background-repeat: no-repeat; */
/*     background-size: 75%; */
    margin: 0px;
    padding: 0px !important;
  }
/*  全局黑色背景  */
  
  div[appid~="1142"],
  div[appid~="4080"],
  li.unreadmail,
  div.meeting_notice_content
  {
    visibility: visible !important;
    background-color:/*[[tilecolor]]*/;
    width: 18% !important;
    min-width: 120px;
    height: 20% !important;
    min-height: 130px;
    position: absolute !important;
    margin: 0 !important;
    padding: 0 !important;
    border: none;
    top: 20% !important;
  }
/*  主磁贴*4  */
  
  
/*==================一卡通开始==================*/
  div.meeting_notice_content{
    left: 10%;
  }
/*  主div  */
  
  .number_blue,
  .span_gray_letter
  {
    height: 1.2em;
    padding: 0 !important;
    margin: 0 !important;
    position: absolute !important;
    font-size: 150%;
    font-family: 黑体;
    color: /*[[themecolor]]*/ !important;
  }
/*  余额 XXX   元   */
  
  .number_blue{
    position: absolute !important;
    right: 1.8em;
    bottom: 0.45em;
  }
/*  数字  */
  
  .span_gray_letter{
    right: 0.5em;
    bottom: 0.3em;
  }
/*  元  */
  
  a.sign_blue{
    position: absolute !important;
    margin: 0px !important;
    padding: 0px !important;
    left: 0;
    width: 100%;
    height: 100%;
    color: rgba(0,0,0,0) !important;
    background-color: /*[[tilecolor]]*/;
  }
/*  点击元素  */
  
  div a.sign_blue:after{
    content: "";
    background-image: url(http://7xrawc.com1.z0.glb.clouddn.com/CampusCard/*[[piccolor]]*/);
    background-position: 50% 20%;
    background-repeat: no-repeat;
    background-size: auto 42%;
    display: block;
    position: absolute;
    width: 100%;
    height: 100%;
    visibility: visible !important;
  }
/*==================一卡通结束==================*/
  
/*==================邮件开始==================*/
  li.unreadmail{
    left: 31%;
  }
/*  磁贴位置  */
  
  div.count{
    position: absolute !important;
    right: 0.5em;
    bottom: 0;
    margin: 0 !important;
    padding: 0 !important;
  }
/*  未读计数位置  */
  
  
  span.highlight{
    color: /*[[themecolor]]*/ !important;
    font-family: 黑体;
    position: absolute !important;
    top: 0;
    right: 0;
    margin: 0 !important;
    height: 1em;
  }
/*  强制设置计数字体和颜色  */
  
  span.label{
    position: absolute !important;
    bottom: 0;
    right: 0;
    margin: 0 !important;
    font-size: 150% !important;
    font-family: 黑体;
    color: /*[[themecolor]]*/ !important;
    width: 4em !important;
    bottom: 0.15em;
    right: 1.6em;
  }
/*    未读邮件 四字    */
  
  
  div.links :nth-child(3)
  {
    position: absolute;
    width: 100%;
    height: 100%;
    left: 0;
    color: rgba(0,0,0,0) !important;
  }
/*  点击元素  */
  
  div.links :nth-child(3):after{
    content: "";
    background-image: url(http://7xrawc.com1.z0.glb.clouddn.com/Mail/*[[piccolor]]*/);
    background-position: 50% 40%;
    background-repeat: no-repeat;
    background-size: auto 55%;
    display: block;
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    visibility: visible !important;
  }
  
/*==================邮件结束==================*/
  
/*==================选课系统开始==================*/
  div.app_btn_gray,
  div.app_btn_white{
    background: none !important;
  }/*  强制取消按钮背景，包括所有 app 元素，这里是选课系统和银校通  */
  
  div[appid~="1142"]
  {
    left: 52% !important;
    background-color: /*[[tilecolor]]*/ !important;
     vertical-align: bottom !important;
  }/*  系统磁贴  */
  
  
  a[appid~="1142"]{
    background: none;
  }
  

  div[appid~="1142"] a.app_btn_gray_name{
    color: rgba(0,0,0,0);
    display: block !important;
    width: 100%;
    height: 100%;
  }
/*  
  原来的a标签，需要利用它的链接，但不易改变文字位置。故构造一个儿子，令它的儿子显示文字标签。
  a标签自身近提供点击功能
  */
  
  div[appid~="1142"] a.app_btn_gray_name:before{
    content: "选课系统";
    display: block;
    position: absolute;
    bottom: 0.2em;
    right: 0.5em;
    color: /*[[themecolor]]*/;
    font-size: 150%;
    font-family: 黑体;
  }
/*  a标签的儿子标签 ，选课系统 四个字 */
  
  div[appid~="1142"] a.app_btn_gray_name:after{
    content: "";
    background-image: url(http://7xrawc.com1.z0.glb.clouddn.com/CourceSelSys/*[[piccolor]]*/);
    background-position: 50% 40%;
    background-repeat: no-repeat;
    background-size: auto 45%;
    display: block;
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    visibility: visible !important;
  }
/*==================选课系统结束==================*/
  
/*==================银校通开始==================*/
  div[appid~="4080"]
  {
    left: 73% !important;
    background-color: /*[[tilecolor]]*/ !important;
  }
/*  系统磁贴  */
  
  a[appid~="4080"]{
    background: none;
  }
  

  div[appid~="4080"] a.app_btn_gray_name{
    color: rgba(0,0,0,0);
    display: block !important;
    width: 100%;
    height: 100%;
  }
/*  原来的a标签  */
  
  div[appid~="4080"] a.app_btn_gray_name:before{
    content: "银校通";
    display: block;
    position: absolute;
    bottom: 0.2em;
    right: 0.5em;
    color: /*[[themecolor]]*/;
    font-size: 150%;
    font-family: 黑体;
  }
/*  a标签的儿子标签 ，选课系统 四个字 */
  
  div[appid~="4080"] a.app_btn_gray_name:after{
    content: "";
    background-image: url(http://7xrawc.com1.z0.glb.clouddn.com/Tuition/*[[piccolor]]*/);
    background-position: 50% 42%;
    background-repeat: no-repeat;
    background-size: auto 45%;
    position: absolute !important;
    top: 0;
    display: block;
    position: absolute;
    width: 100%;
    height: 100%;
    visibility: visible !important;
  }
/*==================银校通结束==================*/
  
/*==================自定义链接开始==================*/
  #TRAIN {
    position: absolute !important;
    margin: 0;
    padding: 0;
    top: 44.5%;
    right: 9%;
    left: 10%;
    height: 50%;
    width: 90%;
    display: block;
  }
/*  自定义链接的容器  */
  
  #TRAIN li
  {
    float: left;
    position: relative;
    background-color: /*[[tilecolor]]*/;
    width: 19.95% !important;
    min-width: 120px;
    height: 40%;
    min-height: 130px;
    margin-right: 3.4%;
    margin-bottom: 4.5%;
    padding: 0;
    border: none;
  }
/*  设置磁贴大小间距以及颜色  */
  
  #TRAIN li a{
    position: absolute;
    font-family: 黑体;
    bottom: 0em;
    right: 0em;
    margin: 0;
    padding: 0;
  }
/*  设置磁贴字体  */
  
  #TRAIN  li a font:nth-child(1){
    font-size: 150%;
    position: absolute;
    color: /*[[themecolor]]*/;
    right: 0.5em;
    bottom: 0.5em;
    width: 5em;
    text-align: right;
  }
/*  磁贴文字位置  */
  
  #TRAIN li:after{
    content: " . ";
    display: block;
    color: /*[[tilecolor]]*/;
    position: absolute;
    top: 0;
    left: 0;
    width: 10px;
    background-color: /*[[tilecolor]]*/;
  }
/*  遮住讨厌的灰点  */
  
  
  #TRAIN > li a.quickhome_item_link{
    width: 100%;
    height: 100%;
  }
/*  提供点击功能  */
  
  p.quickhome_item_close {
    position: absolute;
    top: 0;
    right: 0;
    margin: 0;
    background-image: none;
    width: 20%;
    height: 20%;
    z-index: 10 !important;
  }
/*  “移除” 按钮  */
  
  p.quickhome_item_close:hover:after{
    content: "移除";
    position: absolute;
    font-family: 黑体;
    font-size: 125%;
    color: /*[[themecolor]]*/;
    z-index: 10 !important;
  }
/*  “移除” 的 hover属性  */
  
/*==================添加按钮开始==================*/
  
  span.quick_title_right{
    visibility: visible;
    position: fixed;
    bottom: -15%;
    left: 0;
    width: 100%;
    height: 16%;
    background-color:rgba(0,85,170,0.5);
    color: /*[[themecolor]]*/;
    font-size: 150%;
    transition: 0.4s;
    z-index:7;
  }
/*  下方的菜单横条，未激活状态  */
  
  span.quick_title_right:hover{
    height: 16%;
    bottom: 0;
    background-color:  #0055aa;
  }
/*  横条的激活状态  */
  
  #addTrain{
    font-family: 黑体;
    position: absolute;
    background-color: rgba(255,255,255,0);
    border: 2px solid /*[[themecolor]]*/;
    height: 84.5%;
    max-height: 130px;
    width: 8%;
    bottom: 6%;
    margin: 0;
    left: 5%;
    color: rgba(0,0,0,0) !important;
  }
/*  添加 按钮的点击功能，以及外框  */
  
  #addTrain:after{
    position: absolute;
    left: 0;
    height: 100%;
    width: 100%;
    top: 20%;
    content: "+";
    font-size: 500%;
    text-align: center;
    color: /*[[themecolor]]*/;
  }
/*  加号  */
  
  #addTrain:before{
    content: "添加";
    text-decoration: none;
    color: /*[[themecolor]]*/ !important;
    position: absolute;
    bottom: 0.5em;
    right: calc( 50% - 1em );
  }
/*  “添加” 二字  */
/*==================自定义链接结束==================*/
  
  .user_name a{
    text-decoration: none;
    font-weight: normal;
    color: /*[[namecolor]]*/ !important;
    font-size: 200%;
    position: fixed;
    top:8%;
    right: 9%;
  }/*  用户名  */
  
  
/*==================Metro样式添加对话框==================*/
  #fancybox-content,
  #fancybox-content > div:nth-child(1),
  div.album_popup_p{
    color: /*[[themecolor]]*/;
    font-family: 黑体;
    font-size: 120%;
    position: fixed;
    right: 0;
    top: 0;
    width: 23% !important;
    min-width: 330px;
    height: 100% !important;
    background-color: #0055aa;
    border: none !important;
    padding: 0;
  }
/*  侧边竖条  */
  
  #fancybox-outer{
    position: fixed;
    right: 0;
    top: 0;
    width: 23% !important;
    min-width: 330px;
    height: 100% !important;
  }
  
  
  div.popup_header_9{
    visibility: hidden;
  }
/*  隐藏原添加对话框的图标及文字  */
  
  div.popup_header_9:after{
    color: /*[[themecolor]]*/;
    content: "添加链接";
    visibility: visible;
    position: absolute;
    top: 2em;
    left: 3em;
  }
/*  新的 “添加链接” 四字  */
  
  div.popup_header:not(.popup_header_9),
  div.album_popup_item{
    color: /*[[themecolor]]*/;
    visibility: visible;
    background: none;
    margin: 8px;
    top: 50%;
    padding: 5% 0 0 5%;
  }
/*  设置输入区的提示文字颜色及边距  */
  
  div.popup_header:nth-child(2),div.album_popup_item{
    top: 4%;
    position: relative;
  }
/*  输入区域定位  */
  
  #NAME,#URL{
    z-index: 10 !important;
  }
/*  防止输入框无法输入  */
  
  #fancybox-close{
    position: absolute !important;
    left: 7%;
    top: 3.6em;
  }
/*  “关闭” 按钮  */
  
  .album_popup_input{
    position: absolute;
    width: 60%;
  }
/*  输入框宽度  */
  
  p[name~="msgbox"]
  {
    background: none !important;
    border: none !important;
    font-size: 70%;
  }
/*  “名称不能为空！” 的提示文字  */
  
  #fancy_ok{
    font-size: 85%;
    position: relative;
    border: 2px solid #FFFFFF;
    background: none !important;
    width: 80px;
    height: 1.55em;
    top: 40px;
    left: -230px;
  }
/*  确定 按钮  */
 
  div.popup_header_14{
    visibility: hidden;
  }
/*  隐藏原来 “操作成功” 的图片  */
  
  span.span_margin_left:after{
    visibility: visible !important;
    position: absolute;
    top: 1em;
    left: 0.5em;
    content: "操作成功";
    font-size: 200%;
    color: /*[[themecolor]]*/;
  }
/*  新的 “操作成功” 提示  */
  
  #fancybox-wrap{
    visibility: hidden;
  }
/*  隐藏闪现的对话框  */
  
  #fancybox-content,#fancybox-close{
    visibility: visible;
  }
/*  不应被隐藏的主要元素  */
  
}
