style.css:
@-moz-document domain(eksisozluk.com) {


body{
	background-color: #ebebeb !important;
	font-family: 'Helvetica', 'Open Sans Light', 'Verdana', sans-serif !important;
        color: 4d4d4d !important;
}

a{
        color: #025671 !important;
}

header{
	opacity: 0.9;
}

#aside, #topic-channel-info, #videos {
	width: 150px !important;
	overflow: hidden;
}

.ads{
	display: none !important;
}

.big-thumb{
	height: 112px !important;
}

#videos .big-thumb>a {
	width: 150px !important;
	height: 112px !important;
}

#videos .big-thumb>a:before{
	content: '';
}

#videos .big-thumb>a>img{
	width: 150px !important;
	height: 112px !important;
}

#videos .big-thumb span.video-title{
	width: 150px !important;
	padding: 0 !important;
}

#who-to-follow{
	width: 150px !important;
}

#content-body{
	width: 740px !important;
}

#entry-list{
	font-size: 14px !important;
}

#partial-index{
	padding-right: 8px !important;
}

h2{
font-size: 18px !important;
}

#entry-list>li{
	border-bottom: #d9d9d9 1px solid;
}

#entry-list .content{
	max-height: none !important;
}

.read-more-link-wrapper{
	display: none;
}

#content-body{
	margin-left: 32px !important;
	margin-right: 32px !important;
}

.timeline {
	width: 150px !important;
}

#index-section{
	width: 260px !important;
}

#main{
	margin-left: 260px !important;
}

.topic-list.partial>li>a>small{
	color: #53a245 !important;
}

.topic-list.partial>li>a{
	padding: 6px 48px 6px 0 !important;
}

.topic-list.partial>li{
	border-bottom: #d9d9d9 1px solid;
}

#who-to-follow>.nick-list .actions{
	text-align: left;
}
  
}
