style.css:
@-moz-document url-prefix("https://www.facebook.com"), url-prefix("https://apps.facebook.com") {
/* Make chat popouts taller */

.fbNubFlyout > div > div > div:nth-child(2) {
    height: 400px !important;
}
/* Make chat popouts wider */

._50mz.opened {
    width: 400px !important;
}
/* Make profile images a circle shape */

.fbNubFlyout .img {
    border-radius: 300px !important;
}
#BuddylistPagelet {
    display: none;
}
}
