}

/*Left Nav*/
#leftNav .column1 {
position: relative;
z-index: 3;
width: 100%;
height: 100%;
padding-bottom: 16px;
background: #090909 ! important;
}

/*Left Nav Active*/
#leftNav a.current:after {
position: absolute;
content: "";
display: block;
top: 0;
right: 0;
width: 4px;
height: 41px;
background: #3C3C3C ! important;
}

#leftNav a:hover, #leftNav a.current {
opacity: 1;
background: #090909 ! important;
}

#leftNav .moreColumn {
position: absolute;
overflow: hidden;
left: 185px;
display: block;
width: 0;
background: #090909 ! important;
-webkit-transition: 0.25s ease 0.35s;
transition: 0.25s ease 0.35s;
}

/*Main Page Fix*/
.PublicForum .column.main {
background: none repeat scroll 0 0 #090909 ! important;
padding-left: 175px ! important;
}

/*Thread Title Colour*/
.forumPost .post_title {
color: #E4E4E4 ! important;
cursor: pointer ! important;
font-family: 'OpenSansRegular',Arial,sans-serif ! important;
}

/*Tags*/
element.style {
}
.forumPost .tags a {
font-size: 11px ! important;
color: #C9C9C9 ! important;
}

/*Thread Title Backgroud*/
.forumPost .header_post {
background: none repeat scroll 0 0 #090909 ! important;
}

/*OP Colour*/
#post > .post {
