style.css:
/* i really want this to be global */

:root {
  --main-bg-color: rgba(0, 0, 0, 0.4);
}

html.lang-base-fr body[data-theme-name=none] #wrapper:not(.attract)>#header {
    background-image: none;
    height: 240px;
}

#wrapper {
    margin-top: 50px;
    background-image: none;
    box-shadow: none;
    background-color: var(--main-bg-color) ;
    border-radius: 30px;
    padding: 10px;
}
#wrapper:after {
    background: none;
}
.online_body {
    background: url(https://img.freepik.com/photos-premium/ville-abandonnee-illustrator-fond-usine-steampunk-render_35766-1028.jpg?w=1380) fixed top / cover no-repeat,#100c0b;
}
.row>.cell-small.rw-18 {
    width: 99%;
}

.row>.cell-small.ro-3 {
    margin: auto;
}

div.game-menu-area>div.game-bar {
    box-shadow: none;
}
div.game-menu-area>div.game-bar>div.ulcont.text-menu, div.game-menu-area>div.game-bar>ul.text-menu {
    background: none;
}
div.game-menu-area>div.game-bar>div.ulcont.text-menu:before, div.game-menu-area>div.game-bar>ul.text-menu:before {
    background: none;
}

div.game-menu-area>div.game-bar>div.ulcont.text-menu:after, div.game-menu-area>div.game-bar>ul.text-menu:after {
    background: none;
}
#wrapper>#content #apps {
    left: 20px;
    top: 20px;
}
#optimizer-btn {
    top: 20px;
}
div.game-menu-area {
    top: 90px;
}
#postbox {
    top: 62px;
}
#wrapper>#content {
    margin: 5px;
}
#wrapper>#content .cell-small.background.map-box, #wrapper>#content .cell.background.map-box, #wrapper>#content>.row:not(.external-app)>.cell-small:not(.manual-background).map-box, #wrapper>#content>.row:not(.external-app)>.cell:not(.manual-background).map-box, #wrapper>#content>noscript>.row>.cell-small.map-box, #wrapper>#content>noscript>.row>.cell.map-box {
    border-radius: 13px;
    background: none;
    background-color: var(--main-bg-color);
}
#wrapper>#content .cell-small.background:not(.fact):not(.flavour-quote):not(.edge-top-left):not(.map-box), #wrapper>#content .cell.background:not(.fact):not(.flavour-quote):not(.edge-top-left):not(.map-box), #wrapper>#content>.row:not(.external-app)>.cell-small:not(.manual-background):not(.fact):not(.flavour-quote):not(.edge-top-left):not(.map-box), #wrapper>#content>.row:not(.external-app)>.cell:not(.manual-background):not(.fact):not(.flavour-quote):not(.edge-top-left):not(.map-box), #wrapper>#content>noscript>.row>.cell-small:not(.fact):not(.flavour-quote):not(.edge-top-left):not(.map-box), #wrapper>#content>noscript>.row>.cell:not(.fact):not(.flavour-quote):not(.edge-top-left):not(.map-box) {
    border-radius: 13px;
    background: none;
    background-color: var(--main-bg-color);
}

.forum-note {
    background: #444;
    border: 1px solid #444;
}
.forum-note>b:first-child {
    background: #000;
}
.forum-note.thread-list-fa-thumbtack .forum-thread {
    background: #444;
}
.forum-note.thread-list-fa-thumbtack .forum-thread .count {
    background: #444;
}
.forum-note .forum-thread .count {
    background: #444;
}
.forum-note .forum-thread.forum-thread-unread {
    color: #fff;
    background: #323232;
}
.forum-note .forum-thread {
    background: #6a6a6a;
    color: #fff;
}
.forum-note.thread-list-fa-thumbtack .forum-thread.selected {
    background: #222;
}

.forum-post>.forum-post-header {
    background: var(--main-bg-color);
    border-radius:  10px 10px 0 0;
}
.forum-post {
    background: none;
    border: 1px solid #939393;
    border-radius: 10px;
}
footer {
    background-color: rgba(0, 0, 0, 0.6);
}
