style.css:
@-moz-document url-prefix("https://myhordes.eu") {
:root {
  --main-bg-color: rgba(0, 0, 0, 0.4);
}

html.lang-base-fr body[data-theme-name=none] #wrapper:not(.attract)>#header {
    background-image: none;
    height: 240px;
}
html.lang-base-fr body[data-theme-name=none][data-theme-daytime=night] #wrapper:not(.attract)>#header {
    background-image: none;
}


#wrapper {
    margin-top: 50px;
    background-image: none;
    box-shadow: none;
    background-color: var(--main-bg-color) ;
    border-radius: 30px;
    padding: 10px;
}
#wrapper:after {
    background: none;
}
.online_body {
    background: url(https://img.freepik.com/photos-premium/ville-abandonnee-illustrator-fond-usine-steampunk-render_35766-1028.jpg?w=1380) fixed top / cover no-repeat,#100c0b;
}
.row>.cell-small.rw-18 {
    width: 99%;
}

.row>.cell-small.ro-3 {
    margin: auto;
}

div.game-menu-area>div.game-bar {
    box-shadow: none;
}
div.game-menu-area>div.game-bar>div.ulcont.text-menu, div.game-menu-area>div.game-bar>ul.text-menu {
    background: none;
}
div.game-menu-area>div.game-bar>div.ulcont.text-menu:before, div.game-menu-area>div.game-bar>ul.text-menu:before {
    background: none;
}

div.game-menu-area>div.game-bar>div.ulcont.text-menu:after, div.game-menu-area>div.game-bar>ul.text-menu:after {
    background: none;
}
#wrapper>#content #apps {
    left: 20px;
    top: 20px;
}
#optimizer-btn {
    top: 20px;
}
div.game-menu-area {
    top: 90px;
}
#postbox {
    top: 62px;
}
#wrapper>#content {
    margin: 5px;
}
#wrapper>#content .cell-small.background.map-box, #wrapper>#content .cell.background.map-box, #wrapper>#content>.row:not(.external-app)>.cell-small:not(.manual-background).map-box, #wrapper>#content>.row:not(.external-app)>.cell:not(.manual-background).map-box, #wrapper>#content>noscript>.row>.cell-small.map-box, #wrapper>#content>noscript>.row>.cell.map-box {
    border-radius: 13px;
    background: none;
    background-color: var(--main-bg-color);
}
#wrapper>#content .cell-small.background:not(.fact):not(.flavour-quote):not(.edge-top-left):not(.map-box), #wrapper>#content .cell.background:not(.fact):not(.flavour-quote):not(.edge-top-left):not(.map-box), #wrapper>#content>.row:not(.external-app)>.cell-small:not(.manual-background):not(.fact):not(.flavour-quote):not(.edge-top-left):not(.map-box), #wrapper>#content>.row:not(.external-app)>.cell:not(.manual-background):not(.fact):not(.flavour-quote):not(.edge-top-left):not(.map-box), #wrapper>#content>noscript>.row>.cell-small:not(.fact):not(.flavour-quote):not(.edge-top-left):not(.map-box), #wrapper>#content>noscript>.row>.cell:not(.fact):not(.flavour-quote):not(.edge-top-left):not(.map-box) {
    border-radius: 13px;
    background: none;
    background-color: var(--main-bg-color);
}

.forum-note {
    background: #444;
    border: 1px solid #444;
}
.forum-note>b:first-child {
    background: #000;
}
.forum-note.thread-list-fa-thumbtack .forum-thread {
    background: #444;
}
.forum-note.thread-list-fa-thumbtack .forum-thread .count {
    background: #444;
}
.forum-note .forum-thread .count {
    background: #444;
}
.forum-note .forum-thread.forum-thread-unread {
    color: #fff;
    background: #323232;
}
.forum-note .forum-thread {
    background: #6a6a6a;
    color: #fff;
}
.forum-note.thread-list-fa-thumbtack .forum-thread.selected {
    background: #222;
}

.forum-post>.forum-post-header {
    background: var(--main-bg-color);
    border-radius:  10px 10px 0 0;
}
.forum-post {
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid #939393;
    border-radius: 10px;
}
footer {
    background-color: rgba(0, 0, 0, 0.6);
}
.forum-post.forum-post-new {
    border: 2px solid #ffdc00;
}
.button, .modal-button, button {
    background: radial-gradient(#9f1415, #000000);
    border-radius: 30px;
}
.forum-button, .forum-button-disabled {
    border-radius: 30px;
    background: radial-gradient(#9f1415, #000000);
}
div.game-menu-area>div.game-bar>div.ulcont.text-menu>li.back-dash, div.game-menu-area>div.game-bar>ul.text-menu>li.back-dash {
    border-radius: 30px;
    background: radial-gradient(#9f1415, #000000);
  	height: 25px;
 	 padding-top: 3.5px;
}
div.game-menu-area>div.game-bar>div.ulcont.text-menu>li.back-dash, div.game-menu-area>div.game-bar>ul.text-menu>li.back-dash:hover{
    border-radius: 30px;
    background: radial-gradient(#b32021, #000000);
  	height: 25px;
 	 padding-top: 3.5px;
  }
}

/* Horloge */

div.game-menu-area>div.game-bar>div.ulcont.clock, div.game-menu-area>div.game-bar>ul.clock {
    background: url(https://i.ibb.co/PmC76hz/1881-12-reveil-radio-design-retro-orange-noir-lumi-removebg-preview.png) no-repeat center;
    background-size: 160px 160px;
    height: 90px;
    position: absolute;
    right: 0;
    top: -137px;
    width: 198px;
}

div.game-menu-area>div.game-bar>div.ulcont.clock>li:nth-child(3), div.game-menu-area>div.game-bar>ul.clock>li:nth-child(3) {
    transform: rotate(-3deg);
    letter-spacing: 2px;
    font-size: 2.50rem;
    left: 57px;
    right: 25px;
    top: 30px;
}
div.game-menu-area>div.game-bar>div.ulcont.clock>li:nth-child(4), div.game-menu-area>div.game-bar>ul.clock>li:nth-child(4) {
    color: #999999;
    transform: rotate(270deg);
    cursor: help;
    font-size: 1rem;
    font-weight: 700;
    left: 139px;
    right: 25px;
    text-align: center;
    top: 39px;
    z-index: 995;
}
div.game-menu-area>div.game-bar>div.ulcont.clock>li:first-child, div.game-menu-area>div.game-bar>ul.clock>li:first-child {
    color: #f8f8f8;
    font-size: 1rem;
    font-variant: small-caps;
    height: 12px;
    left: 0;
    right: 9px;
    text-align: right;
    top: 87px;
}
