style.css:
@-moz-document domain("stackstorage.com") {
#tip-wrapper, #tip-header, #tip-content, #tip-footer {
    background-color: #212121;
}

#app-content,#controls,#tip-content  #app-content, #headerName-container	{
    background-color: #353535;
}
table #filestable, #headerCheckbox, #headerName-container, #headerThumbnail, #headerShared, #headerSize, #headerTags, #headerDate, #app-content-files{
background-color: #353535;
}

#fileList .checkbox, #fileList .thumbnail, #fileList .filename,#fileList .filename a,  #fileList .shared, #fileList .filesize, #fileList .tags, #fileList .date, #app-selection-options, .sub-menu	{
    background-color: #666666;
    color: #ffffff !important;
}

#app-selection-options:before	{
    background-color: #666666
}

#tip-footer ul, #tip-mobile-bottom-menu	{
    background-color: #353535;
    color: #ffffff;
}
}
