@-moz-document regexp("(https|http)://www.dmm.com/netgame/social/-/gadgets/=/app_id=854854/"), regexp("(https|http)://osapi.dmm.com/gadgets/.*"), regexp("(https|http)://pc-play.games.dmm.com/play/tohken/") {
/* コピーライトを目立たせる
=========================================================================================== */
#copyright {
    font-size: 1.6em !important;
    color: #f19072 !important; /* 東雲色 */
	padding-bottom: 80px;
}

/* 背景色
=========================================================================================== */
BODY,
#main-ntg {
    background-color: #383c3c !important; /* 羊羹色 */
}

/* フッターのテキスト文字色とリンク文字色
=========================================================================================== */
div#foot {
    color: #f19072 !important;
}
div#foot a,a:link,a:visited {
    color: #f25e30 !important;
}
div#foot a:hover,a:active {
    color: #f19072 !important;
}

/* 参考：和色大辞典　http://www.colordic.org/w/
