	/* ------------------------------------------------ quote expand verlauf -------------------------------------------- */		

	
		.bbCodeQuote .quoteContainer .quoteExpand
	{		
		display: none;
		-webkit-box-sizing: border-box; -moz-box-sizing: border-box; -ms-box-sizing: border-box; box-sizing: border-box;
		position: absolute;
		height: 80px;
		top: 90px;
		left: 0;
		right: 0;
		
		font-size: 11px;
		line-height: 1;
		text-align: center;
		color: rgb(218, 218, 218);
		cursor: pointer;
		padding-top: 65px;
		
		background: -webkit-linear-gradient(top, rgba(247, 242, 248, 0) 80%, rgb(247, 242, 248) 80%);
		background: -moz-linear-gradient(top, rgba(247, 242, 248, 0) 80%, rgb(247, 242, 248) 80%);
