style.css:
@-moz-document domain("mlbpark.donga.com") {
  /* 읽은글 회색 */
  
  A:visited {
      color: #9a9a9a;
  }
  /* 담장 읽은글 색깔 변경*/
  
  .tbl_type01 .t_left a:visited {
      color: #9a9a9a;
  }
  /* 상단 불펜로고 삭제 */
  
  .logo {
      display: none;
  }
  /* Hot 아이콘 삭제 */
  
  .tbl_type01 .icon_hot {
      display: none;
  }
  /* 상단 안 쓰는 메뉴 없애기 */
  
  #PCHeader > .main_menu > .list_menu > li:nth-of-type(4) > .news.top04 {
      display: none;
  }
  #PCHeader > .main_menu > .list_menu > li:nth-of-type(5) > .prestige.top05 {
      display: none;
  }
  /*
  #PCHeader > .main_menu > .list_menu > li:nth-of-type(7) > .dugout.top07 {
      display: none;
  }
  */
  
  .index #PCHeader .main_menu .list_menu li .top09 {
      display: none;
  }
  /* 광고차단 */
  
  .top_ad,
  #left_ad,
  #center_ad02,
  #right_cont {
      display: none;
  }
  #center_ad01,
  .right_menu,
  .picture {
      display: none;
  }
  /* 게시판 전체 크기 */
  
  .index {
      width: 100%;
      min-width: 930px;
      background-color: #FFFFFF;
  }
  .index #container .contents .left_cont {
      width: 1030px;
      margin-bottom: 50px;
  }
  /* 상단, 하단 배경색 */
  
  .footer,
  .index #PCHeader .bg {
      background: #5181e3;
  }
  /* 게시판 헤드 높이 수정 */
  
  .tbl_type01 thead tr td {
      height: 30px;
  }
  .tbl_type01 tbody tr td {
      padding: 0 4px 0 0;
  }
  /* 게시판 글 목록 간격 
.tbl_type01 tbody tr td {height: 32px;}*/
  /* 말머리 수정 */
  
  .tbl_type01 tbody tr td .word,
  .main_contents .nav .bestTitle {
      color: #AAAAAA;
      font-weight: normal;
      display: inline-block;
      text-align: center;
      width: 60px;
  }
  /* 게시판 음영 */
  
  .tbl_type01 tr:nth-child(odd) {
      background-color: #fff;
  }
  .tbl_type01 tr:nth-child(even) {
      background-color: #FaFaFa;
  }
  /*.tbl_type01 tr:hover {background-color: #e0f0ff;}*/
  /*본문 사진 크기*/
  
  #contentDetail img,
  video {
      width: auto !important;
      max-width: 978px !important;
      text-align: center !important;
  }
  /* 게시글 차단글 안보이게 하기 */
  
  *[id^='list_block_'] {
      display: none;
  }
  /* 게시글 내 추천,조회,리플 오른쪽으로 옮기기 */
  
  .view_head .items .text {
      display: inline-block;
      padding: 0 10px 17px 10px;
      width: 1000px;
  }
  /* 이전 게시판 가리기 */
  
  .prev_list {
      display: none;
  }
  /* 검색창 오른쪽으로 이동, 높이 변경 */
  
  .list_search {
      float: left;
      height: 30px;
      width: 318px;
      padding: 0 20px 0 0;
      border: 1px solid #ccc;
      background: #fff;
  }
  /* 촌스런 담장 제목 없애기 */
  
  .tit_today {
      display: none;
  }
  /* 담장 타이틀 스타일 */
  
  .main_contents .nav .bestTitle {
      color: white;
      text-align: center;
      font-size: 105%;
      background: #686868;
      padding: 1px 0 2px 1px;
      font-family: 돋움;
  }
  /* 댓글 갯수 색상 변경 */
  
  .tbl_type01 .replycnt {
      color: #FF8000;
      font-weight: bold;
  }
  /* 댓글 입력 박스 */
  
  .left_cont table {
      width: 100%;
  }
  #contentTable {
      width: 95%;
      margin: 0 0 0 30px;
  }
  /* 댓글 레이아웃 배경 */
  
  .reply_list {
      padding: 10px 0px 10px 0px;
  }
  /*댓글 사진 크기*/
  
  .reply_list .txt_box img,
  video {
      width: auto !important;
      max-width: 978px !important;
      text-align: center !important;
  }
  /* 타인 댓글 설정 */
  /* 타인 댓글 한줄씩 색구분 */
  
  .reply_list .other_reply .txt:nth-child(odd) {
      position: relative;
      float: left;
      max-width: 90%;
      padding: 15px 10px;
      background: rgba( 255, 255, 255, 0);
      border-radius: 0 10px 10px 10px;
      text-overflow: ellipsis;
      text-align: left;
      font-family: "굴림", "Gulim";
  }
  .reply_list .other_con:nth-child(odd) {
      overflow: hidden;
      position: relative;
      margin: 0 0 0 0;
      padding: 10px 0 0 55px;
      background: #f7f7f7
  }
  .reply_list .other_con:nth-child(even) {
      overflow: hidden;
      position: relative;
      margin: 0 0 0 0;
      padding: 10px 0 0 55px;
      background: #fff
  }
  .reply_list,
  #cmtFormTable td {
      background: #f7f7f7 !important;
  }
  .reply_list .other_con .txt .icon_arr {
      display: none;
  }
  .reply_list .other_reply .txt span,
  .tbl_type01 thead tr td {
      color: #000;
  }
  .reply_list .other_reply .txt,
  .reply_list .other_con .txt .icon_arr,
  .reply_list .my_reply .txt {
      background: #f7f7f7;
  }
  /*타인 댓글 포토사진*/
  
  .reply_list .other_con .photo img {
      position: absolute;
      left: 10px;
      border-radius: 20%;
      border: 1px solid #ddd;
  }
  .reply_list .other_con .photo {
      position: absolute;
      top: 10px;
      left: 0;
      width: 37px;
      height: 37px;
      border-radius: 100%;
      border: 0px solid #bfbfbf;
  }
  /*타인 댓글 경계선*/
  
  .reply_list .other_con {
      border-bottom: 1px solid #ccc;
  }
  /*타인 닉네임 설정*/
  
  .reply_list .other_reply .txt_box .name {
      color: #5181e3;
      font-weight: bold;
  }
  /*본인 댓글 설정*/
  /*본인 닉네임 설정*/
  
  .reply_list .my_reply .txt_box .name {
      color: #000;
      font-weight: bold;
  }
  .reply_list .my_con {
      border-bottom: 1px solid #ccc;
  }
  /*본인 댓글 배경색 및 위치*/
  
  .reply_list .my_con {
      overflow: hidden;
      position: relative;
      margin: 0 0 0 0;
      padding: 10px 0 0 55px;
      background: #FFFFE4;
  }
  .reply_list .my_reply .txt {
      float: left;
      background: rgba( 255, 255, 255, 0);
  }
  .reply_list .my_con .photo {
      position: absolute;
      top: 10px;
      left: 10px;
      width: 37px;
      height: 37px;
      border-radius: 20%;
      border: 1px solid #bfbfbf;
  }
  .txt {
      margin-top: -10px;
      margin-bottom: -10px;
  }
  /* 댓글 작성일시 & 아이피 주소의 색상과 글꼴 변경 */
  
  .reply_list .date,
  .reply_list .ip {
      display: inline;
      color: gray !important;
      font-size: 10px !important;
      font-family: Lucida Console;
  }
  /* 댓글 아이피 괄호로 감싸기 */
  
  .reply_list .ip:before {
      content: '['
  }
  .reply_list .ip:after {
      content: ']'
  }
  /* 하단 웹툰 가리기 */
  
  .bigtoon_sub {
      display: none;
  }
  /* 하단 새로운 광고 블록 */
  
  #taboola-below-article-thumbnails {
      display: none;
  }
  #taboola-below-article-thumbnails-2nd {
      display: none;
  }
  #rbox-t2m {
      display: none;
  }
  /* 풋터 블록 */
  
  .footer {
      display: none;
  }
  /*
Date : 20170421
*/
}
