style.css:
@-moz-document domain("forum-3dcenter.org") {

    body {
        background: #000000 url('data:image/jpeg;base64,');
        color: #000000 !important;
    }
    a:link,
    body_alink {
        color: #F09A2D !important;
    }
    a:visited,
    body_avisited {
        color: #EE7500 !important;
    }
    a:hover,
    a:active,
    body_ahover {
        color: #FF0066 !important;
    }
    .page {
        background: transparent;
        color: #ffffff !important;
    }
    .tborder {
        background: #2e2e1e !important;
        color: #ffffff !important;
        border: 1px solid #EE7500 !important;
    }
    .tcat {
        background: #EE7500 !important;
        color: #000000 !important;
    }
    .tcat a:link,
    .tcat_alink {
        color: #ffffff !important;
    }
    .tcat a:visited,
    .tcat_avisited {
        color: #ffffff !important;
    }
    .tcat a:hover,
    .tcat a:active,
    .tcat_ahover {
        color: #CCCCFF !important;
    }
    .thead {
        background: #F09A2D !important;
        color: #000000 !important;
        border-color: #171717 !important;
    }
    .thead a:link,
    .thead_alink {
        color: #ffffff !important;
    }
    .thead a:visited,
    .thead_avisited {
        color: #ffffff !important;
    }
    .thead a:hover,
    .thead a:active,
    .thead_ahover {
        color: #CCCCFF !important;
    }
    .tfoot {
        background: #000000 !important;
        color: #FFFFFF !important;
    }
    .tfoot a:link,
    .tfoot_alink {
        color: #FFFFFF !important;
    }
    .tfoot a:visited,
    .tfoot_avisited {
        color: #FFFFFF !important;
    }
    .tfoot a:hover,
    .tfoot a:active,
    .tfoot_ahover {
        color: #CCCCFF !important;
    }
    .alt1,
    .alt1Active {
        background: #171717 !important;
        color: #ffffff !important;
        border-color: #171717 !important;
    }
    .alt2,
    .alt2Active {
        background: #1f1f1f !important;
        color: #ffffff !important;
        border-color: #171717 !important;
    }
    .inlinemod {
        background: #FFFFCC !important;
        color: #000000 !important;
    }
    .wysiwyg {
        background: #171717 !important;
        color: #ffffff !important;
    }
    .time {
        color: #ffffff !important;
    }
    .highlight {
        color: #FF0000 !important;
        font-weight: bold;
    }
    .fjsel {
        background: #3E5C92 !important;
        color: #E0E0F6 !important;
    }
    .fjdpth0 {
        background: #1f1f1f !important;
        color: #ffffff !important;
    }
    .panel {
        /* background: #000000 url('data:image/jpeg;base64,'); */
        background: #171717;
        color: #fff;
        border: 2px outset lightgreen;
    }
    .panelsurround {
        background: #171717 !important;
        color: #ffffff !important;
    }
    legend {
        color: #fff !important;
    }
    .vbmenu_control {
        background: #000000 !important;
        color: #FFFFFF !important;
    }
    .vbmenu_control a:link,
    .vbmenu_control_alink {
        color: #FFFFFF !important;
    }
    .vbmenu_control a:visited,
    .vbmenu_control_avisited {
        color: #FFFFFF !important;
    }
    .vbmenu_control a:hover,
    .vbmenu_control a:active,
    .vbmenu_control_ahover {
        color: #CCCCFF !important;
    }
    .vbmenu_popup {
        background: #FFFFFF !important;
        color: #000000 !important;
        border: 1px solid #0B198C !important;
    }
    .vbmenu_option {
        background: #1f1f1f !important;
        color: #ffffff;
    }
    .vbmenu_option a:link,
    .vbmenu_option_alink {
        color: #ffffff !important;
    }
    .vbmenu_option a:visited,
    .vbmenu_option_avisited {
        color: #ffffff !important;
    }
    .vbmenu_option a:hover,
    .vbmenu_option a:active,
    .vbmenu_option_ahover {
        color: #ffffff !important;
    }
    .vbmenu_hilite {
        background: #171717 !important;
        color: #FFFFFF !important;
    }
    .vbmenu_hilite a:link,
    .vbmenu_hilite_alink {
        color: #ffffff !important;
    }
    .vbmenu_hilite a:visited,
    .vbmenu_hilite_avisited {
        color: #ffffff !important;
    }
    .vbmenu_hilite a:hover,
    .vbmenu_hilite a:active,
    .vbmenu_hilite_ahover {
        color: #ffffff !important;
    }

    a.bigusername:link {
        color: #ffffff !important;
    }

    a.bigusername:visited {
        color: #ffffff !important;
    }

    a.bigusername:hover,
    a.bigusername:active {
        color: #ffffff !important;
    }

    /* ***** spoiler tags ***** */
    div.spoiler {
        background-color: #2e2e1e !important;
        color: #ffffff !important;
        border: 1px solid #EE7500 !important;
    }
    .spoiler div.spoilerheader {
        background-color: #1f1f1f !important;
        color: #ffffff !important;
    }
    .spoiler .spoilerheader a:link,
    .spoiler .spoilerheader a:visited {
        color: #ffffff !important;
    }
    .spoiler .spoilerheader a:hover,
    .spoiler .spoilerheader a:active {
        color: #FF0066 !important;
    }

    /* DIV container vom Quote und neuer Rahmen */
    .alt1 .QuoteBorder {
        background-color: #232323 !important;
        border: 1px solid #3f3f3f !important;
    }
    .alt2 .QuoteBorder {
        background-color: #171717 !important;
        border: 1px solid #3f3f3f !important;
    }

    a.t-link {
        color: black;
    }
    span.tt-admin,
    span.nt-admin {
        color: #c33;
    }
    span.nt-crew,
    span.tt-crew {
        color: #e64e00 !important;
    }
    span.nt-crew-cc,
    span.tt-crew-cc {
        color: #0dab59 !important;
        color: #4ca4e6 !important;
    }
    span.nt-mod,
    span.tt-mod {
        color: #008000 !important;
    }
    span.tt-admin2 {
        color: #8B0000 !important;
    }
    span.threadstarter {
        color: #0000BE !important;
    }

    /* Hinweise */
    .navbar_notice {
        border-bottom: 1px solid #e64e00 !important;
    }

    /* Hinweis für angepasste Bilder via img BBCode */
    div.resized-img-bar {
        border: 1px solid #EE7500 !important;
        background-color: LemonChiffon;
    }
    .modtext {
        color: #E64E00 !important;
        background: #fff;
        border: 1px solid #E64E00 !important;
    }


    /* style_dreid2.css */
    #container .content-corner {
        background: #00050a url('data:image/jpeg;base64,');
    }

    #gravotechnik {
        display: none;
    }

    /* inline CSS */
    hr {
        color: #171717 !important;
        background-color: #171717 !important;
        border-color: #171717 !important;
    }
    .alt2[align="right"] {
        background: #1F1F1F !important;
    }
    
    /* Icons & Smileys */
    img[src^="images/statusicon"],
    /*img[src^="images/icons"],*/
    img[src="images/3dc/insanedruid/misc/whos_online.gif"],
    img[src="images/3dc/insanedruid/misc/stats.gif"],
    img[src="images/3dc/insanedruid/misc/birthday.gif"] {
      background: #fff;
      border-radius: 100%;
      padding: 1px;
    }
    
    /* img[src^="images/smilies"],
    img[src^="images/3dc/smilies"] */
    
    /* Formulare */
    .fieldset {
        /*  1px solid #EE7500 */
        border: 0 !important;;
        background-color: #1f1f1f;
        padding: 10px;
    }
    fieldset {
        position: relative;
        padding-top: 30px !important;
    }
    fieldset legend {
        position: absolute;
        top: 10px;
        left: 10px;
    }
    #vBulletin_editor_div input {
        border: 0;
        background-color: transparent;
        color: #fff;
        padding: 0;
        outline: none;
    }
    #vBulletin_editor_div textarea {
        background: #1f1f1f;
        color: #fff;
        border: 0;
        outline: none;
        padding: 10px;
    }
    .vBulletin_editor {
        background: transparent;
        border: 0;
        padding: 0;
    }
}
