style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://api.vircities.com/app/index.html") {
  #js-menu {
    display: block !important;
    width: 260px !important;
  }
  #js-canvas {
    margin-left: 260px !important;
    margin-top: -35px !important;
    overflow: hidden !important;
  }
  .js-nested-menu {
    display: none !important;
  }
  .nested-elements {
    display: block !important;
  }
  .js-goto-url-wiki, .js-goto-url-vk, .js-goto-url-fb {
    display: none !important;
  }
  .nested-elements li, [data-screen-id="settings"] {
    padding-left: 5px !important;
    padding-top: 5px !important;
    padding-bottom: 5px !important;
    cursor: pointer !important;
  }
  [data-screen-id="settings"] {
   border-top: 1px solid #31343c;
  }
  .c-menu__panel {
    display: none !important;
  }
  [data-screen-id="shop"], [data-screen-id="referrals"] {
    display: none !important;
  }
  .menu-item-img, .menu-item-img img {
    width: 26px !important;
    height: 26px !important;
  }
  .c-menu {
    padding-top: 0 !important;
  }
  [data-screen-id="news"] .u-clearfix {
    display: none !important;
  }
  [data-screen-id="settings"] .c-menu__panel {
    display: block !important;
    padding-left: 0 !important;
  }
  .o-short-info {
    height: 110px !important;
  }
  .c-menu-separator {
    display: none !important;
  }
}
