    padding: 0.825em 1.075em 0.675em 0.975em;
}

.trow2 {
    background: #1f1f1f;
    padding: 0.825em 1.075em 0.675em 0.975em;
}
  
#container {
    background: rgba(31,31,31, 0.7);
}

body {
    background: url('/*[[bg]]*/');
}

#content {
    background: transparent;
    width: auto!important;
    padding: 40px 10px;
    overflow: hidden;
    background-image: url(https://weeb.sx);
    background-attachment: fixed;
}
    
div.yuieditor-toolbar {
    overflow: hidden;
    padding: 2px 2px 1px;
    background: #242424;
    line-height: 0;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-border-radius: 3px 3px 0 0;
    -moz-border-radius: 3px 3px 0 0;
    border-radius: 3px 3px 0 0;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
}
    
.yuieditor-button yuieditor-button-spoiler {
    background: 242424
}                   
.trow3 {
    background: #242424;
    padding: 0.25em 0.25em 0.675em 0.975em;
    border-bottom: 0px dashed #dadada;
}
    
.navhome {
    display: block;
    float: left;
    padding: 0px 10px;
    position: relative;
    color: #FCFCFC;
    background: #242424;
    box-shadow: 0;
    border-radius: 2px;
    margin-right: 0;
    transition: all .31s ease 0;
    -moz-transition: all .31s ease 0;
    -webkit-transition: all .31s ease 0;
}
.newsTicker .news-header, .newsTicker .newsController {
    border-bottom: 2px solid /*[[color]]*/;
    background: #1F1F1F;
    color: #fff;
    font-family: Sans-Serif,Arial,"Open Sans";
    font-size: 14px;
    font-weight: 500;
}
    
.forum_on {
    background: url(https://puu.sh/oRasR/3a3401d373.png) no-repeat center center;
    background-color: /*[[color]]*/;
    background-size: cover;
}

.forum_off {
    background: url(https://puu.sh/oRasR/3a3401d373.png) no-repeat center center;
    background-size: cover;
}

.forum_offlock {
    background: url(https://puu.sh/oRasR/3a3401d373.png) no-repeat center center;
    background-color: #F00;
    background-size: cover;
}

.rf li:hover > a {
    color: #fff;
    position: relative;
    border-bottom: 0px solid /*[[color]]*/;
}
    
.rf li a {
    display: block;
    text-decoration: none;
    padding: 20px;
    text-align: center;
    color: #FFF;
    border-right: 0px solid #363636;
    border-left: 0px solid #424242;
}

textarea:focus {
    background: #1f1f1f;
    color: #fff;
    border: 1px solid /*[[color]]*/;
    padding: 8px;
    line-height: 1.4;
    outline: 0;
    font-family: Tahoma,Verdana,Arial,Sans-Serif;
    font-size: 13px;
}

textarea {
    background: #1f1f1f;
    color: #fff;
    border: 1px solid #000000;
    padding: 8px;
    line-height: 1.4;
    outline: 0;
    font-family: Tahoma,Verdana,Arial,Sans-Serif;
    font-size: 13px;
}

.rf li ul, .rf li ul li ul {
    position: absolute;
    height: auto;
    min-width: 200px;
    padding: 0;
    margin: 0;
    background: #1f1f1f;
    border-top: 4px solid /*[[color]]*/;
    opacity: 0;
    right: 0;
    left: auto;
    visibility: hidden;
    transition: all 300ms linear;
    -o-transition: all 300ms linear;
    -ms-transition: all 300ms linear;
    -moz-transition: all 300ms linear;
    -webkit-transition: all 300ms linear;
    top: 130px;
    z-index: 1000;
}

.rf li ul:before {
    content: "";
    position: absolute;
    top: -8px;
    right: 23%;
    border-bottom: 5px solid /*[[color]]*/;
    border-left: 5px solid transparent;
    border-right: 5px solid transparent;
}

.headermain {
    height: 100px;
    background: rgba(31,31,31, 0.7);
    margin: 0 auto;
    padding: 0;
    line-height: 100px;
    margin-bottom: 0px;
    width: 100%;
}
    
.smallheader {
    background: rgba(31,31,31, 0.7);
    border-bottom: 1px solid /*[[color]]*/;
    box-shadow: 0px 0px 0px #181818;
    width: 100%;
    height: 40px;
    line-height: 40px;
    margin: 0;
    padding: 0;
    margin-bottom: 20px;
}

.user-nav {
    opacity: 0;
    visibility: hidden;
    pointer-events: none;
    padding: 0;
    size: 12px;
    height: 41px;
    line-height: 41px;
    background: #1f1f1f;
    transition: opacity 250ms;
    margin-top: -1px;
}

.user-nav:before {
    content: "";
    position: absolute;
    top: 15px;
    left: -5px;
    border-right: 5px solid #1f1f1f;
    border-top: 5px solid transparent;
    border-bottom: 5px solid transparent;
}

.pmspace_container {
    background: #1f1f1f;
    border-bottom: 1px solid /*[[color]]*/;
    width: 100px;
    display: inline-block;
}

.user-name {
    white-space: nowrap;
    text-shadow: 1px 1px 5px /*[[color]]*/;
    position: absolute;
    right: 0;
    top: 0;
    padding: 0 10px;
    color: #FFFFFF;
    z-index: 10;
    transition: right 200ms ease;
    box-sizing: border-box;
    border-right: 1px solid #222;
    height: 41px;
    line-height: 41px;
    margin-top: -0px;
    text-transform: uppercase;
    font-weight: 600;
    cursor: pointer;
}

.socialmenu li a:link, .socialmenu li a:visited, .socialmenu li a {
    color: #FFF;
    display: inline-block;
    padding: 0;
}
    
.socialmenu {
    height: 40px;
    line-height: 40px;
    width: auto;
    display: inline-block;
    margin-left: -20px;
    float: left;
    padding: 0;
    border-right: 0px solid #222;
    border-left: 0px solid #3A3A3A;
}
    
.breadcrumb .crust:last-child a.crumb {
    font-weight: bold;
    background: none;
    color: #FFFFFF;
    border-radius: 2px;
}

.tabxselect1 {
    display: inline-block;
    padding: 10px;
    border: 1px solid #000;
    color: white;
    background-color: /*[[color]]*/;
    border-radius: 5px;
}

.patternchanger ul li a, .fixed, .fluid {
    cursor: pointer;
    color: #FFFFFF;
}
    
::-webkit-scrollbar-thumb {
    background-color: /*[[color]]*/;
    border-radius: 0;
}
    
a:visited {
    color: /*[[color]]*/;
    text-decoration: none;
}
    
.go-top {
    position: fixed;
    bottom: 3.4em;
    right: 0;
    text-decoration: none;
