.probuttons {
    background: #242424;
    padding: 4px 8px;
    border-radius: 2px;
    box-shadow: 0px 2px 3px rgba(0,0,0,0.2);
    border-bottom: 1px solid /*[[color]]*/;
}

.prostars {
