options.value:
.message-group-blocked {
    background: #fcfcfc;
    -webkit-box-pack: center;
    -o-box-pack: center;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    border: 1px solid #e3e5e7;
    border-radius: 3px;
    margin: 6px 6px 6px 19px;
}

.guilds-wrapper{
    position:absolute;
    z-index:5;
    left:-80px;
    transition: left ease .5s;
    height:100%;
    background:transparent;
    border-right:solid transparent 50px;
}
.guilds-wrapper:hover {
    left: 0;
}
.channel-members{
    transform:translate(145px);
    z-index:2;
    transition:all 300ms ease;
}
.channel-members:hover{
    animation: member 300ms ease;
    transform:translate(0px);
    background: #000 !important;
}
.channel-members .member .member-activity,
.channel-members .member .member-username-inner{
    opacity:0;
    transition:opacity 300ms ease, margin 100ms ease;
    
}
.channel-members .member .member-username-inner{
    margin-bottom: 6px;
}
.channel-members:hover .member .member-activity, .channel-members:hover .member .member-username-inner{
    opacity:1;
}
.header-toolbar button.active{
    display:none;
}
.channel-members h2{
    transform-origin:left;
    transform:scale(0.7);
    padding:0px 0px 0px 10px;
    transition: all 300ms ease;
    background-color: /*[[Color]]*/;
    color: #fff !important;
    padding: 6px;
}
.channel-members:hover h2{
    transform:scale(1);
    padding: 4px 58px;
}
.channel-members .avatar-small{
    transform:scale(1.2);
}
.channel-members:hover .avatar-small{
    transform:scale(1);
}
.chat.flex-vertical.flex-spacer>.content.flex-spacer.flex-horizontal>.flex-spacer.flex-vertical{
    margin-right:-145px;
    z-index:1
}
.chat.flex-vertical.flex-spacer.private>.content.flex-spacer.flex-horizontal>.flex-spacer.flex-vertical{
    margin-right:0px;
    z-index:1
}
.theme-dark .channel-members .member.popout-open, .theme-dark .channel-members .member:hover {
    background: -webkit-linear-gradient(left, rgba(81, 81, 81, 0.18) 85%, rgba(46, 49, 54, 0)) !important;
}
.guild-channels .channel-voice .channel-arrow {
    order: 2;
}
.guild-channels .channel-voice.collapsed .channel-arrow {
    transform: rotate(-180deg);
}
