style.css:
@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist|ownlist/style/theme).*") {
/* START */

body {
	margin: 0;
	text-align: center;
	font-family: Segoe UI !important;
	font-size: 11px;
  	background: #222 repeat !important;
}

body:before {
    background: #333 !important;
    content: "" !important;
    width: 100% !important;
    height: 50px !important;
    top: 0px !important;
    left: 0 !important;
    position: absolute !important;
    z-index: -1 !important;
    border-bottom: 2px solid #222 !important;
}

body:after {
    background-image: url("/*[[bgnew]]*/") !important;
    
    background-color: #222 !important;
    background-repeat: no-repeat !important;
    background-position: center 25% !important;
    
    background-size: cover !important;
    -moz-background-size: cover !important;
    -webkit-background-size: cover !important;
    
    content: "" !important;
    width: 100% !important;
    height: 200px !important;
    top: 50px !important;
    left: 0 !important;
    position: absolute !important;
    z-index: -2 !important;
    border-bottom: 50px solid #FFF !important;
    
}

body.ownlist_anime_update {
    padding-top: 165px !important;
}

a, a:hover {
    text-decoration: none !important;
    
    -webkit-transition: color 0.5s;
	-moz-transition: color 0.5s;
	transition: color 0.5s;
}

a { color: #222 !important; }

a:hover { color: #658FF3 !important; }

.inputtext {
    padding: 5px !important;
    border-color: #DCDCDC !important;
    
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
}

.page-common .lightLink, .page-common .lightLink:link, .page-common .lightLink:visited, .page-common .lightLink:active {
    color: #777 !important;
}

input[type="button"] {
    background: #F9F9F9 !important;
    padding: 5px 10px 7px 10px !important;
    border: 1px solid #DCDCDC !important;
	color: #CCC !important;
    border-bottom: 3px solid #DCDCDC !important;
    text-transform: lowercase !important;
    font-size: 12px !important;
    cursor: pointer !important;
    
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
    
    -webkit-transition: color 0.5s, background 0.5s;
	-moz-transition: color 0.5s, background 0.5s;
	transition: color 0.5s, background 0.5s;
}

input[type="button"]:hover {
    color: #999 !important;
    background: #FFF !important;
    
    -webkit-transition: color 0.5s, background 0.5s;
	-moz-transition: color 0.5s, background 0.5s;
	transition: color 0.5s, background 0.5s;
}

.page-common .textarea {
    width: 100% !important;
    padding: 6px !important;
    border-color: #DCDCDC !important;
    
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
    
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
}

/* Ads ------------------------------------------*/

.left_bottom_ad, .page-common .side-ad, .side-ad,
#content ~ .mauto.clearfix.pt24[style="width:760px;"] .fl-l,
#content ~ .mauto.clearfix.pt24[style="width:760px;"] .fl-r,
#content .container-right ._unit[style="width:300px; height:250px; display: block !important;"],
#content .js-scrollfix-bottom-rel div[style="padding: 20px 0 20px 40px; float: left; position: relative; z-index: 1;"] ._unit,
#content .js-scrollfix-bottom-rel div[style="padding: 20px 40px 20px 0; float: right; position: relative;"] ._unit,
#content .js-scrollfix-bottom-rel div[style="padding: 20px 0 20px 40px; float: left; position: relative; z-index: 1;"],
#content .js-scrollfix-bottom-rel div[style="padding: 20px 40px 20px 0; float: right; position: relative;"],
#content .js-scrollfix-bottom-rel div[style="padding: 20px 40px;display: inline-block;"] ._unit,
#content .js-scrollfix-bottom-rel div[style="padding: 20px 0px;display: inline-block;"] ._unit,
#content .js-scrollfix-bottom-rel div[style="padding: 20px 40px;display: inline-block;"],
#content .js-scrollfix-bottom-rel div[style="padding: 20px 0px;display: inline-block;"],
#content .right-column .widget .widget.right_middle_ad.right,
#ad-skin-bg-left, #myanimelist ._unit table,
#myanimelist ._unit {
    display: none !important;
    height: 0px !important;
    width: 0px !important;
}

/* Iframe Quick Add ------------------------------------------*/

.quickadd-content #searchResults {
    background: #FFF !important;
    padding: 10px !important;
    
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
    
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

/* Header --------------------------------------------*/

#headerSmall {
    background: url("http://i.imgur.com/j3h08DC.png") no-repeat !important;
    padding-top: 0px !important;
    padding-bottom: 214px !important;
    background-position: 0px 15px !important;
    z-index: 1 !important;
}

.page-common #headerSmall>a.link-mal-logo {
    padding-top: 10px !important;
    width: 60px !important;
    height: 30px !important;
}

#headerSmall a.banner-header-anime-straming {
    display: none !important;
}

#header-menu {
    top: 0px !important;
    height: 48px !important;
    background: #2B2B2B !important;
}

.header-menu-unit {
    padding-top: 12px !important;
}

.header-profile {
    background: none !important;
    padding-top: 0px !important;
}

a.profile-image {
    border-right: 1px solid #649292 !important;
    
    border-radius: 3px 0px 0px 3px !important;
    -moz-border-radius: 3px 0px 0px 3px !important;
    -webkit-border-radius: 3px 0px 0px 3px !important;
}

.header-profile .profile-name {
    color: #FFF !important;
    font-weight: normal !important;
    padding: 0px 16px 0px 10px !important;
}

.page-common .header-profile-button {
    height: 50px !important;
    width: 50px !important;
    background-color: #000 !important;
    
    box-shadow: 0px 0px 0px !important;
}

.page-common .header-list-button, .page-common .header-message-button,
.page-common .header-notification-button {
    color: #CCC !important;
}

.page-common .header-list-button:hover, .page-common .header-message-button:hover,
.page-common .header-notification-button:hover,
.page-common .on .header-list-button, .page-common .on .header-message-button,
.page-common .header-notification-button[aria-expanded="true"] {
    color: #FFF !important;
}

.page-common .header-list-button .fa, .page-common .header-message-button .fa,
.page-common .header-notification-button .fa {
    font-size: 19px !important;
}

.header-notification {
    padding-right: 10px !important;
}

.page-common .header-notification-button.has-unread {
    color: #FFF !important;
}

.page-common .header-notification-button.new:after {
    background-color: #F14747 !important;
}

.page-common .header-notification-button.has-unread:after {
    background-color: #F14747 !important;
    border: none !important;
    padding: 2px 4px 2px 3px !important;
    text-align: center !important;
}

.page-common #header-menu>.border {
    display: none !important;
}

.page-common .header-profile-link {
    color: #CCC !important;
    font-weight: normal !important;
}

.page-common .header-profile-link:hover,
.page-common div.on .header-profile-link {
    color: #FFF !important;
}

.page-common .header-profile-link i.fa-caret-down {
    margin-left: 8px !important;
}

.page-common .button_edit,
.page-common .button_add {
    border-bottom: none !important;
}

/* Menu --------------------------------------------*/

#menu {
    margin-top: 0px !important;
    height: 50px !important;
    background: #333 !important;
    position: absolute !important;
    top: 0 !important;
    
    border-radius: 3px 3px 0px 0px !important;
    -moz-border-radius: 3px 3px 0px 0px !important;
    -webkit-border-radius: 3px 3px 0px 0px !important;
}

#menu_left {
    background: #2B2B2B !important;
    height: 50px !important;
    margin-left: 70px !important;
}

#menu_right {
    padding: 6px 0px 2px 6px !important;
    position: absolute !important;
    background: rgba(0, 0, 0, 0.6) !important;
    
    top: 208px !important;
    left: 0px !important;
    z-index: 1 !important;
    
    border-radius: 3px 3px 0px 0px !important;
    -moz-border-radius: 3px 3px 0px 0px !important;
    -webkit-border-radius: 3px 3px 0px 0px !important;   
}

/* Search --------------------------------------------*/

#searchBar {
    padding: 0px !important;
}

#searchBar .inputtext {
    background: #373737 !important;
    border: none !important;
    height: 30px !important;
    text-indent: 2px !important;
    color: #999 !important;
    
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
}

#topSearchButon { display: none !important; }

#topSearchText { margin-right: 5px !important; }

.page-common #searchBar.searchBar #topSearchText {
    width: 256px !important;
}

#top-search-bar #searchBar .inputtext {
    background: #222 !important;
}

/* Search Dropdown ----------------------------------------*/

.page-common .incrementalSearchResultList {
    position: absolute;
    background-color: #FFF;
    top: 42px !important;
    left: 0px !important;
    width: 349px !important;
    border: #D2D2D2 1px solid !important;
    z-index: 100;
    
    -moz-border-radius: 0px 0px 6px 6px;
    -webkit-border-radius: 0px;
    border-radius: 0px 0px 6px 6px;
    
    -moz-box-shadow: 0px 0px 10px 2px rgba(0,0,0,0.2);
    -webkit-box-shadow: 0px 0px 10px 2px rgba(0,0,0,0.2);
    box-shadow: 0px 0px 10px 2px rgba(0,0,0,0.2);
    
    transition: background-color 0.5s !important;
    -moz-transition: background-color 0.5s !important;
    -webkit-transition: background-color 0.5s !important;
}

.page-common .incrementalSearchResultList .list.separator:first-of-type {
    border-top: 0px !important;
}

.page-common .incrementalSearchResultList .list.separator {
    text-transform: capitalize;
    height: 14px !important;
    line-height: 14px;
    padding: 5px 8px 10px 8px;
    color: #000;
    font-weight: normal !important;
    background-color: #F2F2F2 !important;
    font-size: 12px !important;
    border-bottom: 1px solid #DCDCDC !important;
    border-top: 1px solid #DCDCDC !important;
}

.page-common .incrementalSearchResultList .list {
    border-bottom: 1px solid #DCDCDC !important;
}

.page-common .incrementalSearchResultList .list.focus {
    background-color: #EBEBEB !important;
    
    transition: background-color 0.5s !important;
    -moz-transition: background-color 0.5s !important;
    -webkit-transition: background-color 0.5s !important;
}

.page-common .incrementalSearchResultList .list .off .image {
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
}

.page-common .incrementalSearchResultList .list .off .info.anime .name, 
.page-common .incrementalSearchResultList .list .off .info.manga .name,
.page-common .incrementalSearchResultList .list .on .info.anime .name, 
.page-common .incrementalSearchResultList .list .on .info.manga .name {
    color: #373737 !important;
    font-weight: bold !important;
}

.page-common .incrementalSearchResultList .list .off .info.anime .media-type,
.page-common .incrementalSearchResultList .list .off .info.manga .media-type,
.page-common .incrementalSearchResultList .list .on .info.anime .media-type,
.page-common .incrementalSearchResultList .list .on .info.manga .media-type,
.page-common .incrementalSearchResultList .list .on .info .media-type,
.page-common .incrementalSearchResultList .list .on .info .extra-info {
    color: #999 !important;
}

.page-common .incrementalSearchResultList .list.list-bottom {
    background-color: #373737 !important;
    color: #FFF !important;
}

.page-common .incrementalSearchResultList .list.list-bottom.focus a {
    color: #FFF !important;
}

.page-common .incrementalSearchResultList .list.list-bottom.focus a:hover {
    color: #CCC !important;
}

/* Form --------------------------------------------*/

.form-select-outer {
    height: 30px !important;
}

.form-select-outer, #topSearchValue {
    width: 75px !important;
    margin-right: 8px !important;
    background: #373737 !important;
    
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
}

/* Nav --------------------------------------------*/

#nav li {
    background: none !important;
    padding-top: 0px !important;
    padding-right: 0px !important;
    height: 50px !important;
}

#nav li.small,#nav li.small2 {
    width: auto !important;
}

#nav li a {
    background: none !important;
    font-weight: normal !important;
    color: #CCC !important;
    font-size: 14px !important;
    height: 50px !important;
    text-align: center !important;
    cursor: pointer !important;
    margin-right: 0px !important;
    padding: 10px 12px 0px 8px !important;
    line-height: 30px !important;
    text-transform: lowercase;
    
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
    
    transition: background-color 0.5s, color 0.5s !important;
    -moz-transition: background-color 0.5s, color 0.5s !important;
    -webkit-transition: background-color 0.5s, color 0.5s !important;
}

#nav li a.non-link:hover, #nav li.hover a.non-link, #nav li a[href="http://myanimelist.net/watch"]:hover {
    color: #FFF !important;
    background: #222 !important;
    
    transition: background-color 0.5s, color 0.5s !important;
    -moz-transition: background-color 0.5s, color 0.5s !important;
    -webkit-transition: background-color 0.5s, color 0.5s !important;
}

#nav li ul {
    background: none !important;
    padding-top: 0px !important;
    
    border-radius: 0px 0px 3px 3px !important;
    -moz-border-radius: 0px 0px 3px 3px !important;
    -webkit-border-radius: 0px 0px 3px 3px !important;
}

#nav li ul li {
    background: #222 !important;
    border-left: 1px solid #222 !important;
    border-right: 1px solid #222 !important;
    border-bottom: 1px dashed #373737 !important;
    height: 35px !important;
    padding-bottom: 8px !important;
    
    color: #828282 !important;
    padding: 0px !important;
}

#nav li ul li:last-of-type {
    
    border-bottom: none !important;
    
    border-radius: 0px 0px 3px 3px !important;
    -moz-border-radius: 0px 0px 3px 3px !important;
    -webkit-border-radius: 0px 0px 3px 3px !important;
}

#nav li ul li a {
    text-align: left !important;
    font-size: 11px !important;
    padding: 12px 8px !important;
    text-transform: none !important;
    color: #999 !important;
    background: none !important;
    height: 12px !important;
    line-height: 12px !important;
    
    border: none !important;
}

#nav li ul li a:hover {
    color: #CCC !important;
}

ul#header-menu-profile li:last-of-type {
    border-radius: 0px 0px 3px 3px !important;
    -moz-border-radius: 0px 0px 3px 3px !important;
    -webkit-border-radius: 0px 0px 3px 3px !important;
}

ul#header-menu-profile li {
    background: #222 !important;
    border-left: 1px solid #222 !important;
    border-right: 1px solid #222 !important;
    border-bottom: 1px dashed #373737 !important;
    border-top: none !important;
    
    color: #828282 !important;
    padding: 0px !important;
}

ul#header-menu-profile li a {
    text-align: left !important;
    font-size: 11px !important;
    padding: 12px 0px !important;
    text-transform: none !important;
    color: #999 !important;
    background: none !important;
    height: 12px !important;
    line-height: 12px !important;
    font-weight: normal !important;
    text-indent: 12px !important;
    
    border: none !important;
}

ul#header-menu-profile li a:hover {
    color: #CCC !important;
}

/* Login Buttons --------------------------------------------*/

.page-common #header-menu .header-menu-login .btn-signup,
.page-common #header-menu .header-menu-login .btn-login {
    font-weight: normal !important;
    border: none !important;
    padding: 10px 15px 10px 15px !important;
    line-height: 30px !important;
    background: none !important;
    width: auto !important;
    font-size: 14px !important;
    text-transform: lowercase !important;
    margin: 0 !important;
}

.page-common #header-menu .header-menu-login a.btn-signup,
.page-common #header-menu .header-menu-login a.btn-login {
    color: #CCC !important;
}

.page-common #header-menu .header-menu-login a.btn-signup:hover,
.page-common #header-menu .header-menu-login a.btn-login:hover {
    color: #FFF !important;
    background: #222 !important;
}

.page-common #header-menu .header-menu-login .btn-signup,
.page-common #header-menu .header-menu-login .btn-login,
.page-common #header-menu .header-menu-login a.btn-signup:hover,
.page-common #header-menu .header-menu-login a.btn-login:hover {
    transition: color 0.5s, background 0.5s !important;
    -moz-transition: color 0.5s, background 0.5s !important;
    -webkit-transition: color 0.5s, background 0.5s !important;
}

/* Content --------------------------------------------*/

#contentWrapper {
	background: none !important;
}

#contentWrapper h1, #contentWrapper .h1 {
    background: #FFF !important;
    padding: 16px 14px !important;
    margin-bottom: 12px;
    color: #C3C3C3 !important;
    border-bottom: 0px !important;
    font-size: 14px !important;
    
    border-top-color: #FFF !important;
    border-left: none !important;
    border-right: none !important;
}

#contentWrapper h1 a, #contentWrapper .h1 a, h2 a {
    color: #C3C3C3 !important;
}

#contentWrapper h1 a:hover {
    color: #47A8F1 !important;
}

#contentWrapper h1 span .icon-team-title {
    background: #47A8F1 !important;
    color: #FFF !important;
}

#contentWrapper div h1.h1 a.header-right {
    margin-top: 2px !important;
}

#contentWrapper div h1.h1:nth-of-type(2) {
    margin-top: -45px !important;
}

#contentWrapper .header-right a { 
    color: #CCC !important;
    font-size: 14px !important;
    margin: 19px 0px 17px 0px !important;
}

#contentWrapper .header-right {
    color: #CCC !important;
    font-size: 14px !important;
    margin: 19px 14px 17px 0px !important;
}

#contentWrapper .header-right:hover, #contentWrapper .header-right a:hover {
    color: #999 !important;
}

#content, #contentHome {
    padding-top: 6px !important;
    padding-bottom: 6px !important;
    
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
    
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
}

.normal_header, h2, .widget-header, .user-profile h4,
.forum .forum-side-block .header, .top-rank-header2,
.ranking-digest .ranking-header {
    border: 1px solid #333 !important;
    background: #333 !important;
    padding: 10px 12px 11px 12px !important;
    color: #CCC !important;
    font-weight: normal !important;
    font-size: 12px !important;
    
    /* box-shadow: inset 0px -2px 0px 0px #6D6D6D; */
    
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
}

.icon-top-ranking-page .prev, .icon-top-ranking-page .next {
    padding: 0px 0px 0px 8px !important;
    background: none !important;
    font-size: 12px !important;
    line-height: 15px !important;
}

.normal_header div a, .normal_header span a, h2 a, 
.forum_category a, .widget-header a, .user-profile h4 a,
.forum .forum-side-block .header a {
    color: #999 !important;
}

.normal_header div a:hover, .normal_header span a:hover, h2 a:hover, 
.forum_category a:hover, .widget-header a:hover, .user-profile h4 a:hover,
.forum .forum-side-block .header a:hover {
    color: #CCC !important;
}

.page-common h2.fw-b {
    font-weight: normal !important;
}

.page-common h2.fs18 {
    font-size: 12px !important;
}

.top-rank-header2 span.ff-Verdana {
    display: none !important;
}

.normal_header span {
    line-height: 16px !important;
}

.normal_header div a[href="/rss.php?type=news"] img {
    display: none !important;
}

#panel_right {
    padding-top: 7px !important;
}

#panel_right div[style="padding-bottom:12px"] {
    padding-bottom: 0px !important;
}

.news-list .comment-list .text, .news-list .news-unit .text {
    font-size: 10px !important;
}

#content .about-contents section img {
    max-width: 100% !important;
}

body.index .left-column {
    width: 710px !important;
}

/* Content Widgets  --------------------------------------------*/

article.widget .right_top_ad {
    display: none !important;
}

body.index #content {
    padding: 0px 10px !important;
    border: none !important;
}

#content .left-column {
    border-right: 3px solid #EBEBEB !important;
    padding-right: 10px !important;
}

body.index .right-column {
    padding-left: 10px !important;
    width: 320px !important;
}

body.index .widget {
    margin-bottom: 12px !important;
}

#content article .widget.anime_discussions .widget-content .borderClass:last-of-type {
    border-bottom: none !important;
}

#content article .widget .widget-header {
    margin: 0px !important;
    
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
}

#content article .widget .widget-content {
    background: none !important;
    padding: 8px 0px 0px 0px !important;
    border-top: none !important;
}

#content article .widget .widget-content .forum-topics {
    border-top: 1px solid #EBEBEB !important;
}

