style.css:
@namespace url(http://www.w3.org/1999/xhtml);
/*With love from Freyaday~*/
/*v -0.3*/
@-moz-document domain("thedailywtf.com") {
	/*global*/
	body{background: #000;}
  #navigation{box-shadow: none !important;}
	a{color: #DDD !important;}

  /*articles*/
	.content-container{background: #000 !important;}
	.title{color: #BBB !important;}
	.details{color: #DDD !important;}
	.author-box{background: #222 !important;}
	#article-page{background: #000 !important;}
	.content{color: #888 !important;}	
	.comments{background: #000 !important;}
	a.comments{color: #CCC !important;}
	blockquote{background: rgba(24,24,24,0.5) !important;}
	code, pre{background: #000 !important; color: #AAA !important;}
	.hljs-tag{color:#d33682 !important;}
	.hljs-title{color:#268bd2 !important;}
	.hljs-keyword{color: #555 !important;}
	.article-body > h2{color: #BBB !important;}
	
	/*main page*/
	.article{background: #000 !important; color: #888 !important; box-shadow: none !important; border-width: 0px 0px 0px 10px !important; padding-right: 10px !important;}
	.article:hover{border-width: 0px 10px 0px 10px !important; padding-right: 0px !important;}
	.link-overlay{color: #000 !important;}
	.archives-link {background-color: #000; box-shadow: none}
	/*main page sidebar*/
	.sidebar-section{background: #000 !important; color: #888 !important;}
	
	/*fora*/
	.slideout-panel{background: #000 !important;}
	.navbar{background: #000 !important;}
	.slideout-menu{background: #000 !important;}
	.alert{background: #221 !important; color: #885 !important;}
}
