@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("keep.google.com") {
  .task-content .taskContent .viewTask {
    direction: rtl!important;
    font-family: 'Iranian Sans Web',Roya,Tahoma !important;
  }
  .notranslate {text-align:right;
                font-family: 'Iranian Sans Web',Roya,Tahoma !important;
}
  pre {
    direction: ltr;
  }
  .tx_tl {
    direction: rtl;
    font-family: 'Iranian Sans Web',Roya,Tahoma !important;
  }
  .tx_tl ul, .tx_tl ol {
    padding: 0 40px 0 0;
  }
  body.mce-content-body, .s-update textarea {
    direction: rtl !important;
    font-family: 'Iranian Sans Web',Roya,Tahoma !important;    
  }
