style.css:
@-moz-document domain("masterani.me") {
.ui.fixed.menu,
.ui[class*="top fixed"].menu {
    border-bottom: 2px solid #E28B8B;
    transition: all 0.25s ease-in-out;
}
.ui.fixed.menu:hover {
    border-bottom: 2px solid rgb(230, 179, 179);
}
.ui.big-cover.list .item {
    transition: box-shadow 0.2s ease-in-out;
}
.ui.big-cover.list .item:hover {
    box-shadow: crimson 0px 0px 4px 2px;
}
.ui.big-cover.list .item>.overlay>.actions .action.green:hover {
    color: #D67070;
    text-shadow: red 0px 0px 15px;
}
#library .section .ui.entries.list .item:hover {
    color:#eee;
    background:#581A1A;
    border-left: 24px crimson solid!important;
}
.ui.secondary.vertical.menu .item {
transition:all 0.2s ease-in-out!important;
}
.ui.secondary.vertical.menu .item:hover {
    background-color:rgba(25,25,25,0.5);
}
.ui.master.header .border {
transition:all 0.2s ease-in-out;
    cursor:pointer;
}
.ui.master.header .border:hover {
box-shadow: 0 5px #e50914;
}
@media only screen and (min-width: 1200px){
    .ui.grid.container {width: calc(78% + 2rem)!important;}
}
}
