style.css:
@-moz-document domain("spotify.com") {

/* This hides the ads on the bottom of the album view */
	.ads-leaderboard-container {
		visibility: hidden !important;
	}
	
	/* This hides the ads on the now playing panel */
	#now-playing-widgets {
		visibility: hidden !important;
	}
	
	/* This hides the ads on the top of the site */
	#header.container {
		display: none !important;
	}

	/* UPDATE 19-07-2017 */

	/* This hides the ads on the bottom of the site */
	.ads-container {
		visibility: hidden !important;
	}
}
