    /* https://userstyles.org/styles/99560/square-tab-for-firefox-australis */
    #TabsToolbar {
        background: transparent !important;
        margin-top: 0px !important;
        margin-bottom: 0 !important;
        margin-left: -1px !important;
        height: 33px !important; /*wysokosc kart*/
    }
    
    #main-window:not([privatebrowsingmode="true"])[sizemode="normal"] #TabsToolbar {   
margin-top: -1px !important;
} /*wyrownanie karty na normalnym oknie po zrobieniu 1px linii*/

        #TabsToolbar .arrowscrollbox-scrollbox {
            padding: 0 !important;
            box-shadow: none !important;
        }

        /*#TabsToolbar .tabbrowser-tabs {
            border-right-width: 1px !important;
            border-color: transparent !important;
            min-height: 31px !important;
            margin-left: 20px !important;
        }*/

        #TabsToolbar .tabbrowser-tab {
            /*border-right-width: 1px !important;
            border-color: transparent !important;*/
            border-width: 0px 0px 0px 0px !important;
            -moz-border-top-colors: none !important;
            -moz-border-left-colors: none !important;
            -moz-border-right-colors: none !important;
            -moz-border-bottom-colors: none !important;
            color: rgba(255,255,255,1) !important; /*kolor tekstu kart i separatorow miedzy kartami*/
            /*opacity: 0.9 !important;*/
            padding: 0px 0px !important;
            background: rgba(255,255,255,.0) !important;
            background-clip: padding-box !important;
            transition: all .1s !important;
            margin-top: 1px !important; /*1px line to enable window drag and drop*/
            /*height: 32px !important;*/        /*wysokosc kart*/ /*to jest chujowe*/
        }

        #TabsToolbar .tabs-newtab-button {
            -moz-border-top-colors: none !important;
            -moz-border-left-colors: none !important;
            -moz-border-right-colors: none !important;
            -moz-border-bottom-colors: none !important;
            border-style: solid !important;
            border-color: transparent !important;
            border-width: 1px 1px 0 1px !important;
            background: transparent !important;
            background-clip: padding-box !important;
            transition: all .1s !important;
        }

        /*#TabsToolbar .tabbrowser-tab[afterselected] {
            border-left-color: rgba(0,0,0,0.0) !important;
        }*/

        #TabsToolbar .tabbrowser-tab[selected] {
            /*color: #696969 !important;*/
            color: rgba(255,255,255,1) !important;
            background: rgba(47,47,47,1) !important;
            background-clip: padding-box !important;
            margin-top: -1px !important; /*potrzebne do 1px linii*/
            padding-top: 2px !important; /*potrzebne do 1px linii*/
        }

        #TabsToolbar .tabbrowser-tab:hover:not([selected]) {
            background-color: rgba(40,40,40,1) !important;
            margin-top: -1px !important; /*potrzebne do 1px linii*/
            padding-top: 2px !important; /*potrzebne do 1px linii*/
        }

