style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.flickriver.com") {

/* ==== Flickr WideScreen (Viewer) - FlickRiver v.7 (new7) - PB NO IMAGES
http://www.flickriver.com/photos/pryingopen/popular-interesting/
==== */

/* (new2) HTLM - === */
*{
    box-sizing: border-box !important;
} 

html {
    display: inline-block!important;
    box-sizing: content-box !important;
    margin-bottom: 1px;
    min-height: 100% !important;
    overflow-y: auto!important;
    overflow-x: hidden!important;
}
body.river.viewlarge:not(.fitscreen) ,
body.river.viewlarge.fitscreen ,
body {
    background-color: black;
    color: #777;
    font-family: Arial,Helvetica,sans-serif;
    min-height: 1500px !important;
    width: 98% !important;
}

#login-logout {
    position: absolute;
    right: 10px;
    padding-bottom: 2px;
    top: 35px !important;
    font-size: 0.8em;
}
#header {
    position: relative;
    height: 63px;
    padding-bottom: 0 !important;
    padding-left: 2px;
    padding-top: 0.5em;
    border: medium none;
    overflow: hidden;
    background: #0d0d0d !important;
}
/* (new2) */
body.river.viewlarge:not(.fitscreen) ,
.river ,
.river.viewlarge.fitscreen>center {
    width: 1907px !important;
    margin-left: 0 !important;
    margin-top: -16px !important;
}
.top-toolbar {
    width: 1907px !important;
    height: 48px !important;
    margin-bottom: 24px !important;
    margin-top: -24px !important;
}
.menu {
    margin-top: -20px !important;
    padding-top: 0;
}

/* (new2) POST COMMENT BOX - === */
#post-comment-panel {
    position: fixed !important;
    width: auto !important;
    height: auto !important;
    left: 30% !important;
    top: 13% !important;
    padding: 50px 200px !important;
    border: 2px solid #666;
    background: #000;
    overflow: hidden;
    opacity: 0.9;
}
#post-comment-button {
    display: inline-block !important;
    height: 2em;
    border: 1px solid #444;
    font-size: 0.7em;
    font-weight: bold;
    color: #bbb;
    text-transform: uppercase;
    cursor: pointer;
}


/* (new3) NO PHOTO HERE - 
http://www.flickriver.com/photos/s.yimg.com/popular-interesting/
=== */
/* body.river.viewlarge.fitscreen .photos-page h3 , */
/* .photos-page,  */
/* body.river.viewlarge:not(.fitscreen) .photos-page>h3 , */
/* .photos-page:nth-child(2n+1)>h3 , */
/* .photos-page:not(:only-of-type)>h3 , */

.photos-page>h3 {
    display: inline-block !important;
    width: 1800px !important;
    font-size: 30px !important;
    text-align: center !important;
    background: red !important;
}
/* (new3)"THIS USER DON'T ALLOW...." - 
http://www.flickriver.com/photos/94079893@N06/
=== */
.tools-panel[style="display:none;"] + br + center #photos .photos-page:only-of-type {
    display: inline-block !important;
    width: 1900px ! important;
    transform: scale(0.5) ! important;
    border: 1px solid tomato !important;
    outline: 1px solid tomato !important;
}
.tools-panel[style="display:none;"] + br + center #photos .photos-page:only-of-type   > h3 {
    position: relative !important;
    display: inline-block !important;
    font-size: 20px !important;
    left: 1658px !important;
    margin-top: 342px !important;
    transform: scale(2) !important;
    width: 1890px !important;
}
/* (new2) THUMBNAIL COUNTER (DECIMAL / WITH 0 before) - === */
.photos-page {
    counter-increment: myIndex !important;
}
.photos-page .photo-panel:first-of-type:before {
    position: absolute!important;
    width: auto !important;
    margin-left: -427px !important;
    top: 355px!important;
    padding: 5px 10px 5px 5px !important;
    content: counter(myIndex, decimal);
    content: counter(myIndex, decimal-leading-zero);
    font-size: 40px !important;
    text-align: center !important;
    background: none repeat scroll 0 0 rgba(62, 59, 59, 0.6) !important;
    border-radius: 25px 0 0 25px !important;
    box-shadow: 0 0 2px rgba(162, 160, 160, 0.6) inset !important;
    color: white!important;
    z-index: 10 !important;
}

/* (new7) - PROGRESS LOADING - === */
.river>center {
    display: inline-block !important;
    box-sizing: border-box;
    width: 100% !important;
    margin-top: 20px !important;
}


#photos {
    display: inline-block;
    width: 100% !important;
}

#big-progress{
    position: relative !important;
    position: fixed !important;
    display: inline-block!important;
    float: left !important;
    width: 100% !important;
    height: 20px !important;
    bottom: 30px !important;
}
#progress {
    width: 100% !important;
    height: 20px !important;
    font-size: 0.8em;
    text-align: center !important;
}


/*( new2) TEST - SHOW LARGE - === */
body.river.viewlarge.fitscreen .photo-panel.first .show-large ,
.photo-panel.first .show-large {
    display: block!important;
    font-size: 50px!important;
}

/* (new2) UNTITLED - === */
.photo-panel:not(.large) .photo-meta span.photo-title.untitled a {
    display: none!important;
}


/* (new4) PHOTO PAGE - INTERSTING / FAVORITES - ROW - FLEX === */
/* body.river.viewlarge:not(.fitscreen) .photos-page ,  */
/* body.river.viewlarge.fitscreen .photos-page , */
.photos-page ,
.tools-panel[style="display:none;"] + br + center .top-toolbar + #user-tags + #photos .photos-page:only-of-type ,
.tools-panel[style="display:none;"] + br + center .top-toolbar + #user-tags + #photos .photos-page {
    display: inline-block !important;
    height: 420px !important;
    width: 3000px ! important;
    transform: scale(0.3) ! important;
}

#photos div.photos-page[id=""]:nth-child(2n):last-of-type ,
.photos-page:last-of-type {
    display: inline-block !important;
    float: left !important;
    height: 420px;
    width: 3000px;
    margin-top: 0% !important;
    transform: scale(0.3);
opacity: 0 !important;
transition: all ease 0.7s !important;
/* background: tomato !important; */
}


/* (new2) PHOTO PAGE LEFT */
.photos-page:nth-child(odd) {
    float: left !important;
    clear: left !important;
    margin-left: -830px !important;
    margin-top: -150px ! important;
}

/* (new2) PHOTO PAGE RIGHT */
/* .photos-page:nth-child(even) , */
.photos-page:not(:only-of-type):nth-child(even) ,
#photos div[id=""].photos-page:nth-child(even) {
    float: right !important;
    margin-right: -1200px !important;
    margin-top: -420px ! important;
}


/* (new2) PB AFTER SEEING PICTURE VIEW LARGE - 
NORMAL:
body.river
FITSCREEN:
body.river.viewlarge.fitscreen
- === */



/* (new5) PHOTO PANEL NORMAL - ==== */
body.river.viewlarge:not(.fitscreen) .photo-panel , 
body.river.viewlarge.fitscreen .photo-panel ,
.photo-panel {
    display: inline-block !important;
vertical-align: top !important;
    min-width: 700px!important;
    max-width: 700px!important;
    min-height: 780px!important;
    max-height: 780px!important;
    bottom: 83px !important;
    margin-top: 80px !important;
    padding: 10px 10px !important;
    border: 4px solid gray!important;
/* transform: translateY(40px) !important; */
/* background: green !important; */
}

body.river.viewlarge:not(.fitscreen) .photo-panel:not(.large) , 
body.river.viewlarge.fitscreen .photo-panel:not(.large) ,
.photo-panel:not(.large) {
    position: relative !important;
    left: -20% !important;
    margin-right: 15px !important;
/* background: red !important; */
/* outline: 1px solid violet !important; */
}
body.river.viewlarge:not(.fitscreen) .photo-video-container ,
body.river.viewlarge.fitscreen .photo-panel:not(.large) .photo-video-container ,
.photo-panel:not(.large) .photo-video-container ,
body.river.viewlarge:not(.fitscreen) .photo-img-container,
body.river.viewlarge.fitscreen .photo-panel:not(.large) .photo-img-container, 
.photo-panel:not(.large) .photo-img-container {
    position: relative;
    height: 637px!important;
    width: 672px !important;
    margin-bottom: -108px !important;
/* background: yellow !important; */
/* outline: 1px solid yellow ! important; */
}

body.river.viewlarge:not(.fitscreen) a.noborder  , 
body.river.viewlarge.fitscreen a.noborder  ,
.photos-page a.noborder {
    display: inline-block !important;
    min-width: 650px !important;
    max-width: 650px !important;
}
body.river.viewlarge:not(.fitscreen) a.noborder:last-of-type:after  , 
body.river.viewlarge.fitscreen a.noborder:last-of-type:after  ,
.photos-page a.noborder:last-of-type:after {
    content: "◽" !important;
    content: "◾" !important;
    position: relative !important;
    display: inline-block !important;
    height: 15px !important;
    line-height: 15px !important;
    margin-top: 25px !important;
    font-size: 40px !important;
    color: #222 !important;
    outline: 1px solid black !important;
}
body.river.viewlarge:not(.fitscreen) a.noborder:last-of-type:visited:after , 
body.river.viewlarge.fitscreen a.noborder:last-of-type:visited:after  ,
.photos-page a.noborder:last-of-type:visited:after {
    position: relative !important;
    display: inline-block !important;
    color: red !important;
    outline: 1px solid black !important;
}
/* body.river.viewlarge.fitscreen .photo-panel-img , */
body.river.viewlarge:not(.fitscreen) .photo-panel-img  ,
.photo-panel .photo-panel-img {
    height: auto !important;
    max-height: 600px !important;
    width: auto !important;
}

body.river.viewlarge:not(.fitscreen) .photo-panel-img {
}


/*(new2) */
body.river.viewlarge.fitscreen .photo-panel-img {
    display: inline-block !important;
    max-height: 500px !important;
    width: auto !important;
    max-width: 650px !important;
/* border: 1px solid yellow !important; */
}
.photo-panel-img[largesrc*="https"]{
    display: inline-block !important;
    max-height: 500px !important;
    width: auto !important;
    max-width: 600px !important;
/* border: 1px solid yellow !important; */
}



/* META - HOVER + TRANSITION BACKGROUND */
.photo-panel:not(.large) .photo-meta  {
    position: absolute!important;
    margin-bottom: -60px !important;
    max-height: 5px!important;
    min-height: 5px!important;
    min-width: 395px !important;
    max-width: 395px !important;
    bottom: 87px !important;
    margin-left: 142px!important;
    padding-top: 20px !important;
    background: transparent !important;
    transform: scale(1.7)!important;
/* border: 1px solid yellow ! important; */
}
.photo-panel:not(.large):hover .photo-meta  {
    position: absolute!important;
    margin-bottom: -60px !important;
    max-height: 5px !important;
    min-height: 5px !important;
    min-width: 395px !important;
    max-width: 395px !important;
    bottom: 87px !important;
    margin-left: 142px!important;
    padding-top: 20px !important;
    padding-bottom: 50px!important;
    background: #333333 !important;
    -moz-transition: 1s background ease !important;
    transform: scale(1.7)!important;
/* border: 1px solid yellow ! important; */
}
.photo-panel:not(.large) .photo-meta .photo-title-container  {
    position: absolute!important;
    left: 50px!important;
    top: 18px !important;
}
.photo-panel:not(.large) .photo-meta span.photo-title {
  position: relative!important;
    display: inline-block !important;
    margin-left: 10px !important;
    transform: scale(1.54) !important;
/* outline: 1px solid white ! important; */
}
/* (new2) OWNER- === */
.photo-panel:not(.large) .photo-meta span.photo-owner  {
    position: relative !important;
    position: absolute !important;
    display: inline-block!important;
    float: none!important;
    width: 270px !important;
    margin-left: 0px !important;
    top: -15px !important;
    color: red !important;
    text-align: center !important;
    font-size: 0 !important;
    z-index: 55500!important;
    transform: scale(1.50)!important;
/* outline: 1px solid white ! important; */
}
.photo-panel:not(.large):hover .photo-meta span.photo-owner  {
    position: absolute !important;
    display: inline-block !important;
    float: none !important;
    width: 270px !important;
    top: -10px !important;
    color: gold !important;
    text-align: center !important;
    font-size: 0 !important;
    z-index: 55500 !important;
    transform: scale(1.50)!important;
/* outline: 1px solid white ! important; */
}
.photo-panel:not(.large) .photo-meta span.photo-owner a {
display: inline-block !important;
    min-width: 270px !important;
    max-width: 270px !important;
    margin-left: 0px !important;
    font-size: 15px!important;
    padding: 1px 3px!important;
    text-decoration: none !important;
    border: none !important;
    overflow: hidden!important;
    white-space: nowrap!important;
    text-overflow: ellipsis !important;
}
.photo-panel:not(.large):hover .photo-meta span.photo-owner a {
    margin-top: -5px !important;
    font-size: 18px !important;
    padding: 1px 3px!important;
    text-decoration: none !important;
    border: none !important;
}

/* (new2) UNTITLED - === */
.photo-panel:not(.large) .photo-meta span.photo-title.untitled a {
    display: none!important;
}
/* HOVER + TRANSITION OPACITY / COLOR */
.photo-panel:not(.large) .photo-meta span.photo-title a {
    position: absolute !important;
    display: inline-block !important;
    max-height: 45px!important;
    min-height: 45px!important;
    line-height: 15px !important;
    width: 100% !important;
    min-width: 263px!important;
    max-width: 263px!important;
    top: -13px !important;
    left: -44px !important;
    padding: 25px 5px 0px 5px !important;
    text-align: left !important;
    overflow: hidden !important;
    white-space: nowrap !important;
    text-overflow: ellipsis !important;
    opacity: 0.7 !important;
    color: #D9D6D6 !important;
/* outline: 1px solid white ! important; */
}
.photo-panel:not(.large):hover .photo-meta span.photo-title a {
    position: absolute!important;
    display: inline-block!important;
    max-height: 74px!important;
    min-height: 74px!important;
    line-height: 15px!important;
    min-width: 263px!important;
    top: -15px!important;
    left: -44px!important;
    padding: 25px 5px 0px 5px !important;
    color: peru !important;
    text-align: left!important;
    white-space: pre-line !important;
    overflow: hidden !important;
    overflow-y: auto !important;
    text-overflow: ellipsis!important;
    opacity: 1 !important;
    -moz-transition: 5s opacity ease , 1s color ease, 7s height ease !important;
    background: #333 !important;
/* outline: 1px solid white ! important; */
}

.photo-panel .photo-ops:not(.photo-op-play-video-container) {
    position: absolute;
    vertical-align: middle;
    width: 140px;
    height: 24px !important;
    bottom: 16px !important;

    right: 132px !important;
    text-align: left;
    transform: scale(2.9) !important;
}

/* (new5) PLAY VIDEO  */
.photo-panel .photo-ops.photo-op-play-video-container {
    display: inline-block !important;
    width: 16px !important;
    height: 24px !important;
    left: 50%;
    transform: scale(2.9) !important;
}


/* (new2) */
.photo-more-info-panel.docked {
    position: absolute!important;
    display: inline-block!important;
    max-width: 340px !important;
    height: 380px!important;
    line-height: 1.3em;
    top: 178px!important;
    left: 176px !important;
    padding: 0 10px 2px 10px!important;
    border: 4px dotted #4e4e4e!important;
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0.82) !important;
    overflow: hidden !important;
    overflow-y: auto !important;
    transform: scale(2) !important;
}
.photo-secondary-meta-item ,
.photo-secondary-meta-item.photo-tags >span:not(.title) nobr a ,
.photo-context-pools.photo-secondary-meta-item>a {
    color: #A0A0A0 !important;
    font-size: 19px!important;
}
.photo-secondary-meta-item:hover ,
.photo-secondary-meta-item.photo-tags >span:not(.title) nobr a:hover ,
.photo-context-pools.photo-secondary-meta-item>a:hover {
    color: gold !important;
    font-size: 19px!important;
}
/* (new2) */
.photo-secondary-meta-item >span:first-of-type {
    display: inline-block !important;
    width: 302px !important;
    color: peru !important;
    font-size: 16px!important;
}

/* (new2) */
.close-panel-button, 
.photo-more-info-panel .close {
    position: absolute;
    display: inline-block !important;
    width: 10px !important;
    height: 10px !important;
    right: 2px;
    top: 2px;
    border-radius: 5px !important;
    cursor: pointer;
    overflow: hidden;
    transform: scale(1.5) !important;
    background-color: white !important;
}

.photo-description {
    color: #666;
    margin-top: 1em;
    font-size: 20px !important;
    cursor: default !important;
}
.photo-description.highlight-links span {
    color: peru !important;
    margin-top: 1em;
    font-size: 20px !important;
}
.photo-stats.photo-secondary-meta-item>a ,
.photo-secondary-meta-item .more-less-data-link a {
    color: gold !important;
}
.photo-stats.photo-secondary-meta-item> a:hover ,
.photo-secondary-meta-item .more-less-data-link a:hover {
    color: tomato !important;
}

.photo-license.photo-secondary-meta-item ,
.title {
    cursor: default !important;
}


/* (new2) SUPPP - === */
.photo-protector {
    display: none !important;
}


/* (new2) PHOTO PANEL LARGE - === */
.link-to-photo-single {
    color: #666;
    font-size: 0.8em;
    height: 865px !important;
    margin-top: 4em;
}

.photo-panel.large {
    position: relative!important;
    position: absolute !important;
    display: inline-block !important;
    border: 4px solid gray !important;
    min-height: 1200px !important;
    left: 1800px!important;
    padding: 10px !important;
    transform: scale(1) !important;
    outline: 1px solid yellow !important;
}
.photo-panel.large .photo-img-container{
    position: absolute !important;
    float: none!important;
    left: 0!important;
    transform: scale(2) !important;
    vertical-align: bottom!important;
}
.photo-panel.large .photo-img-container a[name="large"], 
.photo-panel.large .photo-video-container a[name="large"] {
    position: absolute !important;
    display: inline-block !important;
    top: 43px !important;
    transform: scale(2) !important;
}

/* ==== END ==== */

}
