style.css:
@-moz-document url-prefix("http://www.jlptstudy.net/") {
body, tbody, .story {
	background-color: #222;
    color: white;
}
.heading, .lisQuestion {
	color:#FFA200;
	/*background-color: #333;*/
	background-color: transparent;
}
.menuHeading, th {
	background-color: transparent;
}
a:link {
    color: white;
    background-color: transparent;
}
a:visited, .smallNote{
    color: #999999;
    background-color: transparent;
}
a:hover {
    color: #ffe3b3;
    background-color: transparent;
}
a:active {
    color: #FFA200;
    background-color: transparent;
}
.kanjiListBox {
	font-family: Meiryo;
}

table#listing tr:nth-child(odd) {
	background-color: transparent;
}
table#listing tr:nth-child(even) {
	background-color: #333;
}
table#listing {
	background-color: #333;
}
#story {
	background-color: #444;
}
}
