style.css:
@-moz-document domain("www.crunchyroll.com") {
#sidebar.right,
#adblocker_message,
#slidebox,
#social_media,
#message_box,
li.premium,
.showmedia-submenu,
a[href^="/freetrial"],
a[href^="/bugreport"],
.portrait-bubble {
    display: none !important;
}
#template_container.template-container,
#main_content,
#welcome_left {
    width: 95%;
}
#showmedia_video_player,
#video_html5_api,
.video-dimensions {
    width: 94vw;
    height: 80vh;
    background: black;
}
.new-template-body h1 {
    font-size: 12px;
    font-weight: normal;
    text-align: center;
}
.guestbook-spoiler-checkbox {
    float: none;
}
#main_content_white,
.white-wrapper {
    background: rgba(255, 255, 255, 0.2);
}
}
