style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com") {

/* when you get tabbed */
.damncrc-chat .other-hl, .msg[class$=" alt0"] {
	background:#FFFFFF!important;
	border:1px solid #C5E0CB!important;
	border-radius:3px!important;
	margin:3px 0px 3px 0px!important;
}

.damncrc-chat .other-hl span {
	background:transparent!important;
}

/* when you talk */
.damncrc-chat .self-hl, .u-electricjonny {

}

.damncrc-chat .self-hl span {
	background:transparent!important;
}

.msg:not(.other-hl) {
	background:#E6EDE6!important;
	border:1px solid #C5E0CB!important;
	border-radius:3px!important;
	margin:3px 0px 3px 0px!important;
}

.damncrc-chat {
	padding:0px 3px 0px 3px!important;
}
}
