style.css:
@namespace url(http://www.w3.org/1999/xhtml);

/*---------------------------*/
/*        新闻文章页拉宽       */
/*---------------------------*/

@-moz-document regexp("http://[\\w\\.]+\\.qq.com/a/.*"){
  /*文章主体及框架背景去除*/
  div#Main-Article-QQ{background:none !important;}

  /*右栏隐藏*/
  div.side {Display:none !Important;}

  /*文章框架扩宽*/
  div#MainL>.main, .mod-left {width:auto !important;float:none!important;}

  /*子模块扩宽*/
  div.mod-left>.bd, div.mod-left>.bd>.Introduction {width:auto !important;}

  /*框架位置调整*/
  div#Cnt-Main-Article-QQ{margin: 20px !important;}
}
