style.css:
@-moz-document domain('www.facebook.com') {
  /* useless column */
  #leftCol {
    display: none !important;
  }

  /* ads, promoted pages */
  #rightCol {
    display: none !important;
  }

  /* online users */
  #pagelet_sidebar,
  #pagelet_dock {
    display: none !important;
  }

  /* adjust center column */
  #contentCol {
    margin-left: auto !important;
    margin-right: auto !important;
  }
  #contentArea {
    float: none !important;
    margin-left: auto !important;
    margin-right: auto !important;
  }
  #globalContainer,
  .sidebarMode ._50tj {
    width: auto !important;
    padding: 0 !important;
  }
}
