style.css:
@-moz-document domain("youtube.com") {
/* Name:    NeonBlackPlayer
  * Author:  bbang
  * Version: 1.01
  */


/*Timer*/
.ytp-time-duration{
    color: rgba(125,215,105, 1);
    background-color : rgba(0,0,0, .1);   
  text-shadow: 0px 0px 5px rgba(0, 0, 0, 1);
    font-weight: 300;                     
}    
    .ytp-time-separator{
	color: rgba(51,175,51, .99);
  text-shadow: 0px 0px 2px rgba(0, 1, 0, 1);
     font-weight: bolder;
}                        
 .ytp-time-current{      
    color: rgba(125,215,105, 1);
    background-color : rgba(0,0,0, .1);          
  text-shadow: 0px 0px 5px rgba(0, 2, 0, 1);        
     font-weight: 300;                
}
	/*Loading Progress*/
	.html5-load-progress, .ytp-load-progress {
   background:  linear-gradient(to right, rgba(75,5,15, .75) 0%, rgba(135,15,15,.85) 75%, rgba(155,25,25,.95) 97.5%,  rgba(195,25,75, .999) 99.9%, rgba(255,155,155, 1) 100%)!important;
   box-shadow: 1px 0px 3px rgba(200,10,10, .75);
                                            
}
	/*Player Bar Hover*/
    .ytp-hover-progress, .ytp-hover-progress-light{
	background: rgba(155,1750,155, .2)
                                                                                                               
}                                                                               
    /*Progress Bar Container */                                    
    .ytp-progress-list {
    z-index: 39;
    background: rgba(255,255,255,.019);
    box-shadow: 0px 0px 3px rgba(20,20,20, .12);                                             
    height: 100%;                                  
                                                                         
}
	/*Play Progress*/
	.ytp-play-progress{
	background:#00A300;
		
	/*Current Time Bar*/
  background: linear-gradient(to right, rgba(0,75,30, 1) 0%, rgba(0,50,20, 1) 2%, rgba(0,0,0, 1) 11%, rgba(0,10,0, 1) 75.75%, rgba(0,20,0, 1) 85.85%, rgba(0,100,10, 1) 95.95%, rgba(75,255,55, 1) 100%)!important;
	box-shadow: 0px 0px 3px rgba(95, 255, 75, 1), 0px 0px 5px rgba(85, 155, 65, .55);                                    
}
	/* Volume slider */
		.ytp-big-mode, .ytp-volume-slider-handle:before, .ytp-volume-slider-track {
    background: linear-gradient(to right, rgba(0,15,0, .05) 0%, rgba(20,75,20, .5) 45%, rgba(30,125,30, .77) 67%, rgba(33,255,33, .88) 100%);
    box-shadow: 0px 0px 5px rgba(93, 255, 44, .055);                                                                              
}

	/*Volume slider background track*/ 
    .ytp-volume-slider-handle:after {
    left: 4px;
    background: rgba(255,255,255,.05);
                                            

}
  	/*Volume slider handle */
	.ytp-volume-slider-handle{
                                            
	background:rgba(12,36,7, .5);
    box-shadow: 0px 0px 9px rgba(0, 255, 0, 1), 0px 0px 4px rgba(190, 255, 190, 1)                                                              
       
} 
  /* Play Button (dot)*/
	.ytp-scrubber-button, .html5-scrubber-button:hover, .ytp-scrubber-button:hover {
   background:rgba(12,66,7, 1);
    border-color:  #009933 !important;
    box-shadow: 0px 0px 9px rgba(0, 255, 0, 1), 0px 0px 4px rgba(190, 255, 190, 1)         
}	
  /*Play Button (dot) HOVER */
  .html5-scrubber-button:hover, .ytp-scrubber-button:hover{
   background: rgba(0,22,0, .8) !important;
   border-color:  #009933 !important;
   box-shadow: 0px 0px 5px #00bb00, 0px 0px 6px #99ff33, 0px 0px 15px #70d24d !important;
}
	/*Menu Buttons */
             
.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover:not([aria-disabled="true"]):not([disabled]) .ytp-svg-fill-logo-tube-lozenge {
        fill: rgba(221,81,51, .5) !important;
}
 .ytp-settings-button.ytp-hd-quality-badge::after, .ytp-settings-button.ytp-4k-quality-badge::after, .ytp-settings-button.ytp-5k-quality-badge::after, .ytp-settings-button.ytp-8k-quality-badge::after, .ytp-settings-button.ytp-3d-badge::after {
        background-color: rgba(221,81,51, .4)!important;
}  

div.ytp-chrome-controls {
   
    background: linear-gradient(to top, rgba(0,0,0, .9) 0%, rgba(0,0,0, .75) 25%, rgba(0,0,0, .555) 55.55%, rgba(0,0,0, .025) 100%) !important;
 
                                        
}
 .ytp-svg-fill
  {
    fill: #222;
  }

  path
  {
    fill: #344540 !important;
                        
  }
                              

	/* HD(720p..), (if available)*/
  .ytp-settings-button.ytp-hd-quality-badge:after,.ytp-settings-button.ytp-4k-quality-badge:after,.ytp-settings-button.ytp-5k-quality-badge:after,.ytp-settings-button.ytp-8k-quality-badge:after{
	background-color:#af0000;    
    fill: rgba(81,221,51,.6);                    
}	
	/* Background -> offer, notes, subtitles, etc. */
	.ytp-panelpopup{
	background:rgba(60, 177, 8, 0.9) none repeat scroll 0% 0%;
	color:#aaa;
} 
	/*TEXT z panel pop-up*/
	.ytp-menu-label-secondary{
	color:#aaa;
}
	/*autoplay*/
	.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox{
	background: rgba(60, 177, 8, 0.9) none repeat scroll 0% 0%;
}



}
