style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("bing.com") {

    /*
    **   Global
    */
    
    /* ID */
    #id_h, #id_l
    {
        background: transparent !important;
       /*background: #222 !important;*/
    }
    
    /* Settings */
    .sw_pref
    {
       border-color: #222 !important;
    }
    #id_sc {
        /* Replace image */
    }
    
    /* Body */
    body
    {
        background-color: #222 !important;
    }
    
    /* Headings */
    h2, .h2
    {
        color: #c0c0c0 !important;
    }
    h3
    {        
        color: #c0c0c0 !important;
    }
    
    /* Footers */
    p.b_smText {
        color: #868686 !important;        
    }

    /* Text*/
    a, .ftrP11, .ftrP12, .inputTitle, .description, .TaskTitle
    {
       color: #c0c0c0 !important;
       text-decoration: none !important;
    }
    .ftrSe
    {
        color: #868686 !important;
    }
    
    /* Links */
    a:hover
    {
        color: #c0c0c0; 
        text-shadow: white 0.05em 0.05em 0.3em;
        text-decoration: none !important;
    }
    a:visited
    {
        color: #696969 !important;
        text-decoration: none !important;
    }
    
    /* Filters */
    .ftrH, .b_dropdown, .ftrD
    {
        background-color: #222 !important;
        border-color: #222 !important;
    }
    .b_selected, .ftrVS
    {
        background-color: #262626 !important;
    }
    
    /* Reviews */
    .b_factrownosep > li:not(.b_sritem)
    {
        background-color: transparent !important;
    }
    
    /*
    ** Splash Page
    */
    
    #hp_sw_hdr
    {
        background-color: #222 !important;
    }
    
    /*
    **   Web Tab
    */
    
    /* Result boxes */ 
    .b_ans, .b_algo {
        background-color: #222 !important;
    }
    
    /* Snippets */
    p.snippet, span.sn_snip, div.b_mBottom {
        color: #868686 !important;
    }    
    
    /* Source */
    cite
    {
        color: #4c4c4c !important;
    }    
    .b_caption {
        color: #868686 !important;
    }
    
    /* Videos */
    .vidr
    {
        background-color: #262626 !important;
    }
    .va_title_mmftb
    {
        color: #c0c0c0
    }
    div.va_title_mmftb::after
    {
       background: transparent linear-gradient(to right, rgba(238, 238, 238, 0), #262626) repeat scroll 0% 0% !important;
    }
    
    /*
    **   News Tab
    */
    
    /* Action Box */
    ul.actions {
        background-color: #262626 !important;
    }    
    li.actionsTxt {
        color: #c0c0c0 !important;
    }

    
    /*
    **   Images Tab
    */
    
    /* Headers */
    #b_header, #rfPane, #rf_bar, #ftrB
    {
        background-color: #222 !important;
    }
    
    /*
    **
    */
    
    /* Headers */
    #sw_hdr
    {
        background-color: #222 !important;
    }
    
    /* Search Bar */
    #sw_b
    {
        background-color: #fff;
    }
    
    /*
    ** Explore Tab
    */
    
    /* Background */
    .TCThreeColumnIrregular 
    {
        background-color: #222;
    }
    .TCFourColumnMediaCTALayout
    {
        background-color: #333;
    }
    .TCMediaCTANestLayout
    {
        background-color: #333;
    }
    
    .Taskcomp0, .Taskcomp1, .Taskcomp2, .Taskcomp3
    {
        background: transparent;
    }
    
     #taskComp0, #taskComp1, #taskComp2
    {
       background-color: #333 !important; 
    }
    
    /***** Code Editor related *****/
    
    .monaco-editor-background {
        background-color: #272822;
    }
    
    /* selected line
    .monaco-editor .view-line {
        background-color: #3C3D38;
    } */
    
    /* For default text */
    .monaco-editor .token {
        color: #F8F8F2;
    }
    
    /* For parentheses and stars */
    .monaco-editor.vs .token.delimiter {
        color: #F8F8F2;
    }
    
    /* For keywords */
    .monaco-editor .token.keyword {
        color: #0080FF;
    }
}
