style.css:
@-moz-document domain("sznpaste.net") {
/*top button nav*/

.navbar-inner, body {
    background: rgb(0, 0, 0);
}
.navbar .nav > li > a, .navbar .brand, body, .title, .base-block {
	color: #DDD !important;
}
.navbar .nav > li > a, .navbar .brand {
    text-shadow: none;    
}

.title, .base-block {
    background: rgb(32, 32, 32) !important;
}

a:link	  { color: rgb( 51, 153, 255); }
a:visited { color: rgb(252, 202,   3); }
a:hover	  { color: rgb( 64, 250, 243); }
a:active  { color: rgb( 64, 250, 243); }
}
