style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lioden.com") {

body {
    background: url('http://orig02.deviantart.net/4d45/f/2016/006/6/e/purple_by_roselynn1214-d9mz5ej.png')!important;
    background-attachment: fixed!important;
    background-size: 100%!important;
}
h1,
h2,
h3 {
    color: black!important;
}
td.right,
.sub_menu,
.right_odd.xsmall,
.right_locked,
td.right_odd,
span.left,
.customiser-sortable li:nth-child(2n+1) {
    background: #7b4f7f!important;
    color: black!important;
}
table.table td {
    background: #7b4f7f!important;
    color: black!important;
}
th,
td.top,
td.bottom,
div.left {
    background: #59264b!important;
    color: black!important;
}
td.left.b.center,
td.left.b,
td.left,
td.right_cub,
.sub_menu_item_selected,
.unread-msg.small,
.unread-msg.center {
    background: #421c3f!important;
    color: black!important;
}
.unread-msg {
    background: #d9c2d5;
}
a:link,
a:visited {
    color: black!important;
    transition: all ease 1s;
}
a:hover,
a:active {
    color: white!important;
    transition: all ease 2s;
}
.sidebar,
.navbar,
.sub_menu,
.sub_sub_menu {
    background: #59264b!important;
    color: black!important;
}
.navbar {
    border-top-right-radius: 15px;
}
.sidebar {
    border-bottom-right-radius: 15px;
    border-left: 1px solid black;
    text-align: justify;
}
.navbar-default .navbar-nav > li > a {
    color: black!important;
    transition: all ease 1s;
}
.navbar-default .navbar-nav > li > a:hover {
    background: #a684a3;
    transition: all ease 3s;
}
.sub_menu_item_selected {
    background: #a684a3!important;
    color: black!important;
}
.xsmall {
    background: none;
}
.panel {
    box-shadow: none!important;
    border: 1px solid black!important;
    background: #421c3f!important;
    border-radius: 15px;
    color: white!important;
}
.pagination span {
    background: #421c3f;
    border: 1px solid black!important;
}
.chat_light {
    background: #7b4f7f!important;
    color: black!important;
}
.chat_dark {
    background: #421c3f!important;
    color: black!important;
}
.topbar,
.breadcrumb {
    background: #a684a3!important;
    color: black!important;
}
.breadcrumb > .active {
    color: white!important;
}
input[type="submit"],
input[type="button"] {
    color: #d9c2d5!important;
    background: #421c3f!important;
    border: 2px solid black!important;
    box-shadow: none!important;
    text-shadow: none!important;
    transition: all ease 1s;
    border-radius: 15px!important;
}
input[type="submit"]:hover,
input[type="button"]:hover {
    color: #7b4f7f!important;
    background: #421c3f!important;
    border: 2px solid black!important;
    box-shadow: none;
    text-shadow: none;
    border-radius: 15px!important;
}
.progress-bar-warning,
.progress-bar-danger,
.progress-bar-success,
.progress-bar {
    background: #7b4f7f!important;
}
.progress {
    background: #a684a3!important;
    border: 1px solid black;
    border-radius: 15px;
}
.progress div {
    color: black!important;
}
div#lion_image>div {
    background: url('https://dl.dropbox.com/s/pjy5ggr4fterwgc/Simple%20Purple%20Lion%20BG.png')!important;
}
table.table {
    border: 1px solid black!important;
}
input[type="text"] {
    color: black!important;
}
input[type="password"] {
    color: black!important;
}
textarea {
    color: black!important;
}
select {
    color: black!important;
}
div.container.main {
    background: #d9c2d5!important;
    box-shadow: none!important;
    border: 2px solid black!important;
    border-top-right-radius: 15px!important;
    border-bottom-right-radius: 15px!important;
    border-bottom-left-radius: 15px!important;
}
div.col-md-9.col-xs-12 {
    background: none!important;
    border: none!important;
    border-top: 2px solid black!important;
}
.item {
    border: #421c3f!important;
}
.item-footer {
    background: #59264b!important;
    color: black!important;
}
.item-header {
    background: #59264b!important;
    color: black!important;
}
input[type="text"] {
    background: #421c3f;
    color: white;
    border: 1px solid black;
    transition: all ease 1s;
    border-radius: 15px;
}
input[type="text"]:focus {
    background: #d9c2d5;
    color: black;
    border: 1px solid black;
    transition: all ease 1s;
    border-radius: 15px;
}
input[type="password"] {
    background: #421c3f;
    color: white;
    border: 1px solid black;
    transition: all ease 1s;
    border-radius: 15px;
}
input[type="password"]:focus {
    background: #d9c2d5;
    color: black;
    border: 1px solid black;
    transition: all ease 1s;
    border-radius: 15px;
}
textarea {
    background: #421c3f;
    color: white;
    border: 1px solid black;
    transition: all ease 1s;
}
textarea:focus {
    background: #d9c2d5;
    color: black;
    border: 1px solid black;
    transition: all ease 1s;
}
select {
    background: #421c3f;
    color: white;
    transition: all ease 1s;
}
}
