style.css:
@-moz-document domain("vk.com") {

/* ФИД ЛЕНТА, ПРАВЫЙ БЛОК*/
#friends_possible_block,
#ui_rmenu_news,
#ui_rmenu_news_list,
#ui_rmenu_recommended,
#ui_rmenu_articles,
#ui_rmenu_updates,
#feed_filters
{
	display: none !important;
}

/* ФИД ЛЕНТА ВООБЩЕ */
#main_feedsd 
{
	display: none !important;
}

/* НОТИФИКАЦИИ  */

.im-page--top-notice
{
	display: none !important;
}

/* НАХУЙ ТЕБЯ И ТВОЁ МЕНЮ СЛЕВА*/
#l_fav,
#l_mk,
#l_ap,
#l_vid,
#l_ph,
#l_gr,
#l_fr,
#l_nwsf
{
	display: none !important;
}

/* НАВОДЯШКИ НА ТЕКСТ, ХОВЕР*/
.left_label 
{
    transition: all 0.5s linear;
    opacity: 0.0;
}

.left_label:hover
{
    opacity: 1.0;
    transition: all 0.5s linear;
}

/* ФУТЕР СЛЕВА */
.left_menu_nav_wrap
{
	display: none !important;
}

/*  СКРЫТО, НО ПРИ НАВЕДЕНИИ ПОКАЗЫВАЕТСЯ */

/* ПАБЛИКИ НА ГЛАВНОЙ СТРАНИЦЕ ПОЛЬЗОВАТЕЛЯ И ВИДЕО */
.page_block > #profile_idols,
#profile_idols,
#profile_videos,
#profile_friends
{
    transition: all 0.5s linear;
    opacity: 0.0;
}

#profile_idols:hover,
#profile_videos:hover,
#profile_friends:hover
{
    opacity: 1.0;
    transition: all 0.5s linear;
}

/* МУСОР */
.profile_rate_warning,
.tt_w,
.lastfm_status
{
	display: none !important;
}

.placeholder { 
    opacity:0 !important;
}

.no_current_info {
    visibility: hidden;
}


/* СПИСОК УЧАСТНИКОВ И  В ПАБЛИКАХ*/
#public_followers,
#public_albums,
#public_videos,
#public_audios,
#public_events,
#public_contacts,
#public_links,
#group_topics,
#group_followers,
#group_albums,
#group_videos,
#group_audios,
#group_events,
#group_contacts,
#group_links,
#group_topics,
#group_apps,
#profile_common_friends,
#profile_friends_online
{
    transition: all 0.5s linear;
    opacity: 0.0 !important;
}

#public_followers:hover,
#public_albums:hover,
#public_videos:hover,
#public_audios:hover,
#public_events:hover,
#public_contacts:hover,
#public_links:hover,
#public_topics:hover,
#group_followers:hover,
#group_albums:hover,
#group_videos:hover,
#group_audios:hover,
#group_events:hover,
#group_contacts:hover,
#group_links:hover,
#group_topics:hover,
#group_apps:hover,
#profile_common_friends:hover,
#profile_friends_online:hover
{
    opacity: 1.0 !important;
    transition: all 0.5s linear;
}

/* IM */
/* LEFT MENU */
#ui_rmenu_unread,
._im_last_act
{
	display: none !important;
}

/* UNREAD */
._im_mess_unread,
._im_mess_unread:before,
.nim-dialog--inner-text
{
    background: transparent !important; 
}

#im_dialogs
{
    padding-bottom: 0px;
}

/* НА СТРАНИЦЕ ПОСЛЕДНИЙ ЗАХОД */
#profile_time_lv {
	display: none !important;
}

.post_like,
.post_share_icon,
.reply_link_wrap,
.page_cover,
.post_share_count,
.post_like_count,
#public_market,
.feed_friends_recomm
{
	display: none !important;
}
}
