style.css:
@-moz-document domain("youtube.com") {
.browse-list-item-container .compact-shelf .yt-uix-shelfslider-prev,
.compact-shelf .yt-uix-shelfslider-prev {
    left: -40px;
    border-right: 0;
}

.browse-list-item-container .compact-shelf .yt-uix-shelfslider-next,
.compact-shelf .yt-uix-shelfslider-next {
    right: -40px;
    border-left: 0;
}

.browse-list-item-container .compact-shelf .yt-uix-button-shelf-slider-pager,
.compact-shelf .yt-uix-button-shelf-slider-pager {
    width: 40px;
    border: 1px solid #e3e3e3;
    box-shadow: 1px 1px 3px rgba(0, 0, 0, .1);
}
}
