style.css:
@-moz-document domain("dribbble.com") {
div.full-tabs {
    background: rgba(0, 0, 0, 0.75);
    border-bottom: 1px solid rgba(0, 0, 0, 0.15);
}
div.full-tabs ul.full-tabs-links>li.active>a {
    color: #B3B3B3;
}
#wrap-inner {
    background: rgba(6,6,6,0.83);
}
ol.dribbbles li.group div.dribbble {
    background: rgba(0,0,0,0.3);
    border-radius: 3px;
}
.form-sub, .form-btn {
    background: /*[[Color]]*/; /* /*[[Color]]*/ */
    transition: .3s all ease-in-out;
}
#footer-inner {
    background: rgba(0,0,0,0.87);
}
#footer {
    border-top: 1px solid rgba(0, 0, 0, 0.89);
}
.form-btn:hover, .form-btn:focus {
    opacity: 0.8;
    transition: .2s all ease-in-out;
    background-color: /*[[Color]]*/;
}
h4#pixels-total {
    background-image: url(http://i.imgur.com/ynXwglZ.png);
    background-size: 55px 55px;
    -webkit-filter: invert(50%);
}
#footer p.follow a img {
    -webkit-filter: invert(100%);
    transition: .3s all ease-in-out;
}
ol.dribbbles li.group div.dribbble a.dribbble-over {
    background: rgba(0, 0, 0, 0.69);
}
ol.dribbbles li.group div.dribbble a.dribbble-over strong {
    color: #CECECE;
}
ol.dribbbles li.group div.dribbble a.dribbble-over em {
    color: #A7A7A7;
}
h1 span.badge-pro, span.badge-pro, h1 span.badge-team, span.badge-team {
    background-color: /*[[Color]]*/;
}
.form-sub:hover, .form-sub:focus {
    opacity: 0.8;
    background-color: /*[[Color]]*/;
}
input.search-text {
    background-color: #565656;
}
input.search-text:focus {
    color: #C5C5C5;
}
div.full-tabs ul.full-tabs-links ul {
    background: rgb(39, 39, 39);
}
div.full-tabs ul.full-tabs-links ul li a:hover, div.full-tabs ul.full-tabs-links ul li span.empty:hover {
    color: #C7C7C7;
    background-color: #2F2F2F;
    opacity: 1;
}
div.full-tabs ul.full-tabs-links>li a span.count, div.full-tabs ul.full-tabs-links>li span.empty span.count {
    font-weight: 500;
    color: #9A9A9A;
}
.overlay-content {
    background: rgba(0, 0, 0, 0.6);
}
h1 {
    color: #B1B1B1;
}
ul.shot-stats>li {
    border-bottom: 2px solid /*[[Color]]*/;
}
ul.shot-stats>li .stats-action img {
    -webkit-filter: invert(100%);
}
h3.meta-head {
    border-top: 2px solid /*[[Color]]*/;
    color: #757575;
}
#comments-section h2.section, #comments-section h2.section-bold {
    border-top: 2px solid /*[[Color]]*/;
}
ol.comments li h2 a {
    color: /*[[Color]]*/;
}
ol.comments li h2 a:hover {
    color: /*[[Color]]*/;
    opacity: 0.8;
}
ol.comments li.response {
    border-bottom: 2px solid /*[[Color]]*/;
}
div.shot-desc p:first-child {
    color: #BFBFBF;
}
div.shot-desc p {
    color: #9E9E9E;
}
div.ad-shot {
    border-top: 2px solid /*[[Color]]*/;
}
#kbd-legend p {
    border-top: 2px solid /*[[Color]]*/;
}
h2.section, h2.section-bold {
    color: #B9B9B9;
}
a:visited {
    color: /*[[Color]]*/;
    opacity: 0.8;
}
h3.meta-head a {
    color: #A8A8A8;
}
div.the-shot div.single {
    background: rgba(0, 0, 0, 0.49);
}
.hover-card {
    background-color: rgba(0,0,0,0.82);
}
div.profile-head h1 a.url {
    color: #FFF;
}
.hover-card .hover-stats li a .count {
    color: /*[[Color]]*/;
}
.hover-card div.profile-head {
    border-bottom: 2px solid /*[[Color]]*/; /* rgba(0, 0, 0, 0.68) */
}
.hover-card::after {
    border-color: transparent transparent rgba(0,0,0,0.82) rgba(0,0,0,0.82);
}
ol.comments li div.comment-body p {
    color: #A9A9A9;
}
form.gen-form fieldset input[type=text], form.gen-form fieldset input[type=password], form.gen-form fieldset input[type=email] {
    color: #B3B3B3;
}
form.gen-form label {
    color: #E6E6E6;
}
a.pro-motion, a.team-motion, a.playbook-motion {
    background: #5B5B5B;
}
a.pro-motion em, a.team-motion em, a.playbook-motion em {
    color: #CCC;
}
a.pro-motion strong, a.team-motion strong, a.playbook-motion strong {
    color: #FFF;
}
ul.vertical-sidenav {
    background: #272727;
    border: 1px solid #171717;
}
ul.vertical-sidenav li.active a {
    color: /*[[Color]]*/;
}
ul.vertical-sidenav li {
    border-bottom: 1px solid #171717;
}
ul.vertical-sidenav li a:hover {
    color: #CACACA;
    background: #212121;
}
form.with-messages fieldset {
    color: #BBB;
}
div.form-section h3 {
    border-top: 2px solid /*[[Color]]*/;
}
h3 {
    color: #C6C6C6;
}
form.gen-form fieldset input[type=text]:focus, form.gen-form fieldset input[type=password]:focus, form.gen-form fieldset input[type=email]:focus, form.gen-form fieldset textarea:focus {
    background: #2a2a2a;
    color: #B3B3B3;
}
.auth-nonlink {
    color: #DCDCDC;
    border: 1px solid #272727;
    background-color: rgba(255, 255, 255, 0.05);
}
form.plain h2 {
    border-bottom: 2px solid /*[[Color]]*/;
    color: #E2E2E2;
}
h2.section, h2.section-bold {
    border-bottom: 2px solid /*[[Color]]*/;
}
p.generic, div.site p, div.col-about p {
    color: #979797;
}
/* Scrollbar */ 

::-webkit-scrollbar {
    width: 10px;
}
::-webkit-scrollbar-thumb {
    background-color: /*[[Color]]*/; 
    border-radius: 0;
}
::-webkit-scrollbar-thumb:hover {
    opacity: 0.9;
}
::-webkit-scrollbar-track {
    background-color: #1F1F1F;
}

/* End Scrollbar */
ol.dribbbles li.group h2 a img, ol.dribbbles li.constrained-content h2 a img, ol.dribbbles li.constrained-content-alt h2 a img, ol.dribbbles li#header h2 a img, ol.dribbbles li#header-inner h2 a img, ol.dribbbles li.player-list-with-filters h2 a img {
    float: none;
    width: 16px;
    height: 16px;
    margin: 1px 5px 0 0;
    border-radius: 50%;
}
ol.dribbbles li.group h2, ol.dribbbles li.constrained-content h2, ol.dribbbles li.constrained-content-alt h2, ol.dribbbles li#header h2, ol.dribbbles li#header-inner h2, ol.dribbbles li.player-list-with-filters h2 {
    max-height: 16px;
    margin: 10px 0 0 0;
    padding: 1px 0px 7px 0px;
    font-size: 13px;
    font-weight: 500;
    line-height: 1.4;
    color: #B3B3B3;
    vertical-align: middle;
    background: #212020;
    text-align: center;
    border-radius: 3px;
}
.profile-info {
    background: #202020;
}
.profile-info-mod {
    border-bottom: 2px solid /*[[Color]]*/;
}
.profile-info-mod h2.vcard .url {
    color: #cacaca;
}
.profile-info-mod .bio {
    color: #969696;
}
.profile-info-mod .skills a {
    border: 2px solid /*[[Color]]*/;
    color: #c7c7c7;
}
.profile-info-mod h3 {
    color: #fbfbfb;
}
.profile-info-mod .skills a:hover {
    color: #ffffff;
    border-color: /*[[Color]]*/;
}
.profile-info-mod ul.profile-details li a {
    color: #dadada;
}
.profile-info-mod ul.profile-details li a img {
    -webkit-filter: invert();
    filter: invert() !important;
}
.profile-info-mod ul.profile-projects li a {
    color: #d2d2d2;
}
ol.list-of-scrolling-rows li.scrolling-row {
    border-bottom: 2px solid /*[[Color]]*/;
}
div.full-tabs ul.full-tabs-links>li a span.label, div.full-tabs ul.full-tabs-links>li span.empty span.label {
    font-weight: 500;
    color: #dcdcdc;
}
div.search-extra {
    border-bottom: 1px solid #2d2d2d;
    background: #2d2d2d;
}
div.search-extra ol.search-extra-list li a {
    color: #bfbfbf;
}
div.search-extra ol.search-extra-list li.more {
    border-right: 1px solid #3d3d3d;
}
body#search-results input.search-text {
    color: #e0e0e0;
}
::selection {
    background: /*[[Color]]*/ !important;
}
}
